// Enhanced dynamic web scraping - Improved for detailed content
const useSelenium = $input.item.json.use_selenium;
const targetUrl = $input.item.json.target_url;
const timeout = ($input.item.json.timeout || 30) * 1000;
const userAgent = $input.item.json.scraper_metadata?.user_agent || 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36';

if (useSelenium) {
  // Use browser automation for dynamic content
  try {
    // Since n8n doesn't have built-in browser automation,
    // we'll use a more sophisticated HTTP approach with retries
    const response = await fetch(targetUrl, {
      method: 'GET',
      headers: {
        'User-Agent': userAgent,
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8',
        'Accept-Language': 'en-US,en;q=0.9,tr;q=0.8',
        'Accept-Encoding': 'gzip, deflate, br',
        'Cache-Control': 'no-cache',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1',
        'Sec-Fetch-Dest': 'document',
        'Sec-Fetch-Mode': 'navigate',
        'Sec-Fetch-Site': 'none'
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    }

    let html = await response.text();

    // If the HTML seems incomplete (just head section), try alternative approach
    if (html.length < 5000 || !html.includes('class=') || !html.includes('<main')) {
      console.log('Initial HTML seems incomplete, trying alternative headers...');

      // Try with different headers that might bypass some restrictions
      const response2 = await fetch(targetUrl, {
        method: 'GET',
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:109.0) Gecko/20100101 Firefox/115.0',
          'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,*/*;q=0.8',
          'Accept-Language': 'en-US,en;q=0.5',
          'Accept-Encoding': 'gzip, deflate',
          'DNT': '1',
          'Connection': 'keep-alive',
          'Upgrade-Insecure-Requests': '1'
        }
      });

      if (response2.ok) {
        const html2 = await response2.text();
        if (html2.length > html.length) {
          html = html2;
        }
      }
    }

    return {
      html: html,
      method: 'selenium_enhanced',
      url: targetUrl,
      timestamp: new Date().toISOString(),
      html_length: html.length
    };

  } catch (error) {
    return {
      html: '',
      method: 'selenium_failed',
      error: error.message,
      url: targetUrl,
      timestamp: new Date().toISOString()
    };
  }
} else {
  // Basic HTTP request
  try {
    const response = await fetch(targetUrl, {
      method: 'GET',
      headers: {
        'User-Agent': userAgent,
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8'
      }
    });

    const html = await response.text();

    return {
      html: html,
      method: 'http_basic',
      url: targetUrl,
      timestamp: new Date().toISOString(),
      html_length: html.length
    };

  } catch (error) {
    return {
      html: '',
      method: 'http_failed',
      error: error.message,
      url: targetUrl,
      timestamp: new Date().toISOString()
    };
  }
}

// Parse Scan Information from HTML using same logic as Python scraper
const html = $input.item.json.html;
if (!html) {
  return { scan_information: {}, error: 'No HTML content' };
}

// Create a simple HTML parser function
function parseHTML(htmlString) {
  // Since we can't use cheerio/jsdom in n8n, we'll use regex parsing
  const results = {};

  try {
    // Look for Scan Information section
    const scanInfoMatch = htmlString.match(/<h4[^>]*>\s*Scan Information\s*<\/h4>[\s\S]*?<table[^>]*>([\s\S]*?)<\/table>/i);

    if (scanInfoMatch) {
      const tableContent = scanInfoMatch[1];

      // Extract business name (look for bizname class)
      const bizNameMatch = tableContent.match(/<span[^>]*class=["']?[^"']*bizname[^"']*["']?[^>]*>([^<]+)<\/span>/i);
      if (bizNameMatch) {
        results['ƒ∞≈ületme Adƒ±'] = bizNameMatch[1].trim();
      }

      // Extract address (look for center-block class)
      const addressMatch = tableContent.match(/<span[^>]*class=["']?[^"']*center-block[^"']*["']?[^>]*>([^<]+)<\/span>/i);
      if (addressMatch) {
        results['Adres'] = addressMatch[1].trim();
      }

      // Extract rating and reviews
      const ratingMatch = tableContent.match(/title=["']?([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5["']?/i);
      if (ratingMatch) {
        results['Puan'] = ratingMatch[1].replace(',', '.');
      } else {
        results['Puan'] = 'N/A';
      }

      // Extract keyword
      const keywordMatch = tableContent.match(/<td[^>]*>\s*Keyword[\s\S]*?<\/td>\s*<td[^>]*>([^<]+)<\/td>/i);
      if (keywordMatch) {
        results['Anahtar Kelime ve Dil'] = keywordMatch[1].trim();
      }

      // Extract date
      const dateMatch = tableContent.match(/<td[^>]*class=["']?[^"']*cnv_dt_lcl[^"']*["']?[^>]*>([^<]+)<\/td>/i);
      if (dateMatch) {
        results['Tarih'] = dateMatch[1].trim();
      }
    }

    // Also look for Rank Summary section
    const rankSummaryMatch = htmlString.match(/<h4[^>]*>\s*Rank Summary\s*<\/h4>[\s\S]*?<table[^>]*>([\s\S]*?)<\/table>/i);

    if (rankSummaryMatch) {
      const rankTableContent = rankSummaryMatch[1];

      // Extract ranked locations
      const rankedMatch = rankTableContent.match(/<td[^>]*>\s*Ranked Locations[\s\S]*?<\/td>\s*<td[^>]*>([^<]+)<\/td>/i);
      if (rankedMatch) {
        results['Ranked Locations'] = rankedMatch[1].trim();
      }

      // Extract average rank
      const avgRankMatch = rankTableContent.match(/<td[^>]*>\s*Average rank[\s\S]*?<\/td>\s*<td[^>]*>([^<]+)<\/td>/i);
      if (avgRankMatch) {
        results['Average rank'] = avgRankMatch[1].trim();
      }

      // Extract best rank
      const bestRankMatch = rankTableContent.match(/<td[^>]*>\s*Best rank[\s\S]*?<\/td>\s*<td[^>]*>([^<]+)<\/td>/i);
      if (bestRankMatch) {
        results['Best rank'] = bestRankMatch[1].trim();
      }
    }

  } catch (error) {
    console.log('HTML parsing error:', error.message);
    results.error = error.message;
  }

  return results;
}

const scanInfo = parseHTML(html);

return {
  scan_information: scanInfo,
  html_length: html.length,
  parsing_method: 'regex_enhanced'
};

