// JavaScript veri √ßƒ±karma (Python js_extractor.py klon)
const htmlContent = $input.first().json.data;

// JavaScript verilerini √ßƒ±kar
function extractJavaScriptData(html) {
  const jsData = {};

  try {
    // Script etiketlerini bul
    const scriptMatches = html.match(/<script[^>]*>([\s\S]*?)<\/script>/gi) || [];

    scriptMatches.forEach((script, index) => {
      const scriptContent = script.replace(/<\/?script[^>]*>/gi, '');

      // JSON verilerini bul
      const jsonMatches = scriptContent.match(/(?:var|let|const)\s+\w+\s*=\s*(\{[\s\S]*?\});?/g) || [];
      jsonMatches.forEach((match, jsonIndex) => {
        try {
          const jsonStr = match.replace(/(?:var|let|const)\s+\w+\s*=\s*/, '').replace(/;$/, '');
          const parsed = JSON.parse(jsonStr);
          jsData[`json_data_${index}_${jsonIndex}`] = parsed;
        } catch (e) {
          // JSON parse hatasƒ±, devam et
        }
      });

      // Map/location verilerini bul
      if (scriptContent.includes('lat') && scriptContent.includes('lng')) {
        const latMatch = scriptContent.match(/lat["']?\s*:\s*([\d.-]+)/);
        const lngMatch = scriptContent.match(/lng["']?\s*:\s*([\d.-]+)/);
        if (latMatch && lngMatch) {
          jsData.map_coordinates = {
            lat: parseFloat(latMatch[1]),
            lng: parseFloat(lngMatch[1])
          };
        }
      }

      // API endpoint'leri bul
      const apiMatches = scriptContent.match(/["']\/api\/[^"']+["']/g) || [];
      if (apiMatches.length > 0) {
        jsData.api_endpoints = apiMatches.map(match => match.replace(/["']/g, ''));
      }
    });

    // Window objelerini bul
    const windowMatches = html.match(/window\.[\w.]+\s*=\s*[^;]+;/g) || [];
    windowMatches.forEach((match, index) => {
      jsData[`window_data_${index}`] = match;
    });

  } catch (error) {
    jsData.extraction_error = error.message;
  }

  return jsData;
}

const extractedJsData = extractJavaScriptData(htmlContent);

return [{
  json: {
    javascript_verileri: extractedJsData,
    extraction_timestamp: new Date().toISOString()
  }
}];

