// Enhanced JavaScript Data Extraction
const html = $input.first().json.data;

if (!html || typeof html !== 'string') {
  return { error: 'No HTML content received' };
}

// Extract pinz data with multiple patterns
function extractPinzData(htmlContent) {
  const patterns = [
    /var\s+pinz\s*=\s*(\[[\s\S]*?\]);/i,
    /window\.pinz\s*=\s*(\[[\s\S]*?\]);/i,
    /pinz\s*:\s*(\[[\s\S]*?\])/i
  ];

  for (const pattern of patterns) {
    const match = htmlContent.match(pattern);
    if (match && match[1]) {
      try {
        let jsonStr = match[1]
          .replace(/\btrue\b/gi, 'true')
          .replace(/\bfalse\b/gi, 'false')
          .replace(/\bnull\b/gi, 'null');
        return JSON.parse(jsonStr);
      } catch (e) {
        continue;
      }
    }
  }
  return [];
}

// Extract scan GUID
function extractScanGuid(htmlContent) {
  const patterns = [
    /scan_guid["']?\s*[:=]\s*["']([a-f0-9-]{8,})["']?/i,
    /data-scan-guid\s*=\s*["']([a-f0-9-]{8,})["']?/i
  ];

  for (const pattern of patterns) {
    const match = htmlContent.match(pattern);
    if (match && match[1]) return match[1];
  }
  return '';
}

// Extract Place ID
function extractPlaceId(htmlContent) {
  const patterns = [
    /(ChIJ[A-Za-z0-9_-]{10,})/g,
    /place_id["']?\s*[:=]\s*["']([A-Za-z0-9_-]{10,})["']?/i
  ];

  for (const pattern of patterns) {
    const matches = htmlContent.match(pattern);
    if (matches) {
      if (pattern.global && matches[0]) return matches[0];
      if (matches[1]) return matches[1];
    }
  }
  return '';
}

const pinzData = extractPinzData(html);
const scanGuid = extractScanGuid(html);
const placeId = extractPlaceId(html);

return {
  html_content: html,
  javascript_data: {
    pinz_array: pinzData,
    scan_guid: scanGuid,
    place_id: placeId,
    pinz_count: pinzData.length
  }
};

// Parse Summary Information
const data = $input.first().json.data || [];

const summary = {};

// Extract business name
for (const item of data) {
  if (typeof item === 'string') {
    // Business name (usually in h1)
    if (!summary['business_name'] && item.length > 5 && item.length < 100) {
      summary['business_name'] = item.trim();
    }

    // Rating
    const ratingMatch = item.match(/([0-9]\.[0-9])\s*out\s*of\s*5/i);
    if (ratingMatch) summary['rating'] = ratingMatch[1];

    // Reviews
    const reviewMatch = item.match(/\((\d+)\s*reviews?\)/i);
    if (reviewMatch) summary['reviews'] = reviewMatch[1];

    // Ranking info
    const rankMatch = item.match(/best\s*rank[:\s]*(\d+)/i);
    if (rankMatch) summary['best_rank'] = rankMatch[1];
  }
}

return { summary_info: summary };

// Parse Competitors
const data = $input.first().json.data || [];
const competitors = [];

for (const html of data) {
  if (typeof html === 'string') {
    const competitor = {};

    // Name
    const nameMatch = html.match(/<[^>]*class[^>]*name[^>]*>([^<]+)/i) || 
                     html.match(/<h[0-9][^>]*>([^<]+)</i);
    if (nameMatch) competitor.name = nameMatch[1].trim();

    // Rating
    const ratingMatch = html.match(/([0-9]\.[0-9])\s*out\s*of\s*5/i);
    if (ratingMatch) competitor.rating = ratingMatch[1];

    // Reviews
    const reviewMatch = html.match(/\((\d+)\s*reviews?\)/i);
    if (reviewMatch) competitor.reviews = reviewMatch[1];

    // Address
    const addressMatch = html.match(/<[^>]*address[^>]*>([^<]+)/i);
    if (addressMatch) competitor.address = addressMatch[1].trim();

    // Website
    const websiteMatch = html.match(/href="([^"]+)"/i);
    if (websiteMatch) competitor.website = websiteMatch[1];

    if (competitor.name) competitors.push(competitor);
  }
}

return { competitors: competitors };

// Parse Sponsored Listings
const data = $input.first().json.data || [];
const sponsored = [];

for (const html of data) {
  if (typeof html === 'string') {
    const listing = {};

    // Name
    const nameMatch = html.match(/<[^>]*class[^>]*title[^>]*>([^<]+)/i) ||
                     html.match(/<h[0-9][^>]*>([^<]+)</i);
    if (nameMatch) listing.name = nameMatch[1].trim();

    // Description
    const descMatch = html.match(/<[^>]*class[^>]*desc[^>]*>([^<]+)/i);
    if (descMatch) listing.description = descMatch[1].trim();

    // Link
    const linkMatch = html.match(/href="([^"]+)"/i);
    if (linkMatch) listing.link = linkMatch[1];

    if (listing.name) sponsored.push(listing);
  }
}

return { sponsored_listings: sponsored };

// Combine Final Data
const allData = $input.all();

// Extract data from each input
const jsData = allData.find(item => item.json.javascript_data)?.json.javascript_data || {};
const summary = allData.find(item => item.json.summary_info)?.json.summary_info || {};
const competitors = allData.find(item => item.json.competitors)?.json.competitors || [];
const sponsored = allData.find(item => item.json.sponsored_listings)?.json.sponsored_listings || [];

// Enhanced map data from pinz
const mapData = [];
if (jsData.pinz_array && jsData.pinz_array.length > 0) {
  jsData.pinz_array.forEach((pin, index) => {
    mapData.push({
      index: index + 1,
      latitude: pin.lat || 'N/A',
      longitude: pin.lng || 'N/A',
      title: pin.title || 'N/A',
      rank: pin.rank || 'N/A'
    });
  });
}

// Final clean data structure
const finalData = {
  business_info: {
    name: summary.business_name || 'Unknown',
    rating: summary.rating || 'N/A',
    reviews: summary.reviews || '0',
    best_rank: summary.best_rank || 'N/A'
  },
  competitors: competitors,
  sponsored_listings: sponsored,
  map_data: mapData,
  javascript_data: {
    scan_guid: jsData.scan_guid || '',
    place_id: jsData.place_id || '',
    total_pins: jsData.pinz_count || 0
  },
  metadata: {
    scraped_at: new Date().toISOString(),
    total_competitors: competitors.length,
    total_sponsored: sponsored.length,
    total_map_points: mapData.length,
    scraper_version: '5.0-clean'
  }
};

return finalData;

