// Enhanced JavaScript Data Extraction - Full Version
// Equivalent to js_extractor.py module

const htmlContent = $input.first().json.data || $input.first().json.output || '';

if (!htmlContent || typeof htmlContent !== 'string') {
  return { 
    error: 'No HTML content received',
    javascript_data: {
      pinz_array: [],
      scan_guid: '',
      place_id: '',
      pinz_count: 0
    }
  };
}

// Safe JSON parsing with multiple fallbacks
function safeJsonLoads(text) {
  if (!text) return [];

  try {
    return JSON.parse(text);
  } catch (e) {
    try {
      // JavaScript boolean/null values to Python equivalents
      let cleanText = text
        .replace(/\btrue\b/gi, 'true')
        .replace(/\bfalse\b/gi, 'false')
        .replace(/\bnull\b/gi, 'null')
        .replace(/\bundefined\b/gi, 'null')
        .replace(/,\s*}/g, '}')
        .replace(/,\s*]/g, ']')
        .replace(/([{,]\s*)([a-zA-Z_$][a-zA-Z0-9_$]*)\s*:/g, '$1"$2":'); // Quote unquoted keys

      return JSON.parse(cleanText);
    } catch (e2) {
      console.log('JSON parse error:', e2.message);
      return [];
    }
  }
}

// Extract pinz data with multiple patterns (equivalent to extract_pinz_data)
function extractPinzData(html) {
  const patterns = [
    /var\s+pinz\s*=\s*(\[[\s\S]*?\]);/i,
    /window\.pinz\s*=\s*(\[[\s\S]*?\]);/i,
    /pinz\s*:\s*(\[[\s\S]*?\])/i,
    /['"]pinz['"]\s*:\s*(\[[\s\S]*?\])/i,
    /let\s+pinz\s*=\s*(\[[\s\S]*?\]);/i,
    /const\s+pinz\s*=\s*(\[[\s\S]*?\]);/i
  ];

  for (const pattern of patterns) {
    const match = html.match(pattern);
    if (match && match[1]) {
      const pinzData = safeJsonLoads(match[1]);
      if (Array.isArray(pinzData) && pinzData.length > 0) {
        console.log(`Found pinz data with pattern ${patterns.indexOf(pattern) + 1}: ${pinzData.length} items`);
        return pinzData;
      }
    }
  }

  console.log('No pinz data found');
  return [];
}

// Extract scan_guid with multiple patterns (equivalent to extract_scan_guid)
function extractScanGuid(html) {
  const patterns = [
    /scan_guid["']?\s*[:=]\s*["']([a-f0-9-]{8,})["']?/i,
    /scan[_-]?guid["']?\s*[:=]\s*["']([a-f0-9-]{8,})["']?/i,
    /["']scan_guid["']\s*:\s*["']([a-f0-9-]{8,})["']?/i,
    /data-scan-guid\s*=\s*["']([a-f0-9-]{8,})["']?/i,
    /window\.scan_guid\s*=\s*["']([a-f0-9-]{8,})["']?/i
  ];

  for (const pattern of patterns) {
    const match = html.match(pattern);
    if (match && match[1] && match[1].length >= 8) {
      console.log(`Found scan_guid: ${match[1]}`);
      return match[1];
    }
  }

  console.log('No scan_guid found');
  return '';
}

// Extract place_id with multiple patterns (equivalent to extract_place_id)
function extractPlaceId(html) {
  const patterns = [
    /(ChIJ[A-Za-z0-9_-]{10,})/g,
    /place_id["']?\s*[:=]\s*["']([A-Za-z0-9_-]{10,})["']?/i,
    /["']place_id["']\s*:\s*["']([A-Za-z0-9_-]{10,})["']?/i,
    /data-place-id\s*=\s*["']([A-Za-z0-9_-]{10,})["']?/i,
    /google\.maps\.places\.PlaceId\s*=\s*["']([A-Za-z0-9_-]+)["']?/i,
    /window\.place_id\s*=\s*["']([A-Za-z0-9_-]+)["']?/i
  ];

  for (const pattern of patterns) {
    const matches = html.match(pattern);
    if (matches) {
      if (pattern.global) {
        // For ChIJ pattern, return first valid match
        for (const match of matches) {
          if (match.startsWith('ChIJ') && match.length > 15) {
            console.log(`Found place_id (ChIJ): ${match}`);
            return match;
          }
        }
      } else if (matches[1] && matches[1].length >= 10) {
        console.log(`Found place_id: ${matches[1]}`);
        return matches[1];
      }
    }
  }

  console.log('No place_id found');
  return '';
}

// Extract all JavaScript data (equivalent to extract_all_js_data)
const pinzData = extractPinzData(htmlContent);
const scanGuid = extractScanGuid(htmlContent);
const placeId = extractPlaceId(htmlContent);

// Additional validation
const isValidGuid = scanGuid.length >= 8 && /^[a-f0-9-]+$/i.test(scanGuid);
const isValidPlaceId = placeId.length >= 10;

// Extract map data from pinz (equivalent to extract_map_data)
const mapData = [];
if (pinzData && Array.isArray(pinzData)) {
  pinzData.forEach((pin, index) => {
    if (pin && typeof pin === 'object') {
      mapData.push({
        index: index + 1,
        lat: pin.location?.lat || pin.lat || null,
        lon: pin.location?.lon || pin.lng || pin.longitude || null,
        label: pin.lable || pin.label || pin.title || '',
        title: pin.title || '',
        url: pin.url || '',
        color: pin.color || '',
        rank: pin.rank || null,
        distance: pin.distance || null
      });
    }
  });
}

return {
  html_content: htmlContent,
  javascript_data: {
    pinz_array: pinzData,
    scan_guid: scanGuid,
    place_id: placeId,
    pinz_count: pinzData.length,
    validation: {
      valid_guid: isValidGuid,
      valid_place_id: isValidPlaceId,
      html_length: htmlContent.length,
      has_pinz_data: pinzData.length > 0
    }
  },
  map_data_from_js: mapData
};

// Parse Scan Information (equivalent to parse_scan_information)
const data = $input.first().json.data || [];
const results = {};

// Helper function to safely get text
function getText(htmlStr, selector) {
  if (!htmlStr) return 'N/A';
  try {
    const matches = htmlStr.match(new RegExp(`<[^>]*class[^>]*${selector}[^>]*>([^<]+)<`, 'i'));
    return matches ? matches[1].trim() : 'N/A';
  } catch (e) {
    return 'N/A';
  }
}

// Helper function to extract rating and reviews
function extractRatingAndReviews(htmlStr) {
  let rating = 'N/A';
  let reviews = '0';

  try {
    // Extract rating from title
    const ratingMatch = htmlStr.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
    if (ratingMatch) {
      rating = ratingMatch[1].replace(',', '.');
    }

    // Extract rating from style width
    if (rating === 'N/A') {
      const styleMatch = htmlStr.match(/width:\s*([0-9]+)%/);
      if (styleMatch) {
        const pct = parseInt(styleMatch[1]);
        rating = (pct * 5 / 100).toFixed(1);
      }
    }

    // Extract review count
    const reviewMatch = htmlStr.match(/\(?\s*([0-9]+)\s*(?:Reviews?|Yorum(?:lar)?|Deƒüerlendirme)?\s*\)?/i);
    if (reviewMatch) {
      reviews = reviewMatch[1];
    }
  } catch (e) {
    console.log('Rating extraction error:', e.message);
  }

  return { rating, reviews };
}

for (const htmlStr of data) {
  if (typeof htmlStr === 'string') {
    // Extract business name
    const bizNameMatch = htmlStr.match(/<span[^>]*class[^>]*bizname[^>]*>([^<]+)</i);
    if (bizNameMatch) results['ƒ∞≈ületme Adƒ±'] = bizNameMatch[1].trim();

    // Extract address
    const addrMatch = htmlStr.match(/<span[^>]*class[^>]*center-block[^>]*>([^<]+)</i);
    if (addrMatch) results['Adres'] = addrMatch[1].trim();

    // Extract rating and reviews
    const ratingInfo = extractRatingAndReviews(htmlStr);
    results['Puan'] = ratingInfo.rating;
    results['Yorum Sayƒ±sƒ±'] = ratingInfo.reviews;

    // Extract keyword and language
    const kwMatch = htmlStr.match(/Keyword[^<]*</td>\s*<td[^>]*>([^<]+)</i);
    if (kwMatch) results['Anahtar Kelime ve Dil'] = kwMatch[1].trim();

    // Extract date
    const dateMatch = htmlStr.match(/<td[^>]*class[^>]*cnv_dt_lcl[^>]*>([^<]+)</i);
    if (dateMatch) results['Tarih'] = dateMatch[1].trim();
  }
}

return { scan_information: results };

// Parse Rank Summary (equivalent to parse_rank_summary)
const data = $input.first().json.data || [];
const results = {};

for (const htmlStr of data) {
  if (typeof htmlStr === 'string') {
    // Extract all table rows
    const rowMatches = htmlStr.match(/<tr[^>]*>[\s\S]*?</tr>/gi) || [];

    for (const row of rowMatches) {
      const cellMatches = row.match(/<td[^>]*>([\s\S]*?)</td>/gi) || [];

      if (cellMatches.length >= 2) {
        const keyCell = cellMatches[0].replace(/<[^>]*>/g, '').trim();
        const valueCell = cellMatches[1].replace(/<[^>]*>/g, '').trim();

        // Map English keys to Turkish
        const keyMappings = {
          'ranked locations': 'Ranked Locations',
          'average rank': 'Average rank (Ranked Locations)',
          'avg total rank': 'Avg total rank (All Locations)',
          'best rank': 'Best rank',
          'max distance': 'Max Distance'
        };

        const normalizedKey = keyCell.toLowerCase();
        for (const [pattern, turkishKey] of Object.entries(keyMappings)) {
          if (normalizedKey.includes(pattern)) {
            results[turkishKey] = valueCell;
            break;
          }
        }
      }
    }
  }
}

return { rank_summary: results };

// Parse Competitors (equivalent to parse_competitors)
const data = $input.first().json.data || [];
const competitors = [];

// Helper function to safely get text
function getText(htmlStr, pattern) {
  try {
    const match = htmlStr.match(pattern);
    return match ? match[1].trim() : 'N/A';
  } catch (e) {
    return 'N/A';
  }
}

// Helper function to extract rating and reviews
function extractRatingAndReviews(htmlStr) {
  let rating = 'N/A';
  let reviews = '0';
  let combined = 'N/A';

  try {
    // Extract rating
    const ratingMatch = htmlStr.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
    if (ratingMatch) {
      rating = ratingMatch[1].replace(',', '.');
    } else {
      // Try style width
      const styleMatch = htmlStr.match(/width:\s*([0-9]+)%/);
      if (styleMatch) {
        const pct = parseInt(styleMatch[1]);
        rating = (pct * 5 / 100).toFixed(1);
      }
    }

    // Extract reviews
    const reviewMatch = htmlStr.match(/\(?\s*([0-9]+)\s*(?:Reviews?|Yorum)?\s*\)?/i);
    if (reviewMatch) {
      reviews = reviewMatch[1];
    }

    // Create combined format
    if (rating !== 'N/A' && reviews !== '0') {
      combined = `${rating} (${reviews})`;
    } else if (rating !== 'N/A') {
      combined = rating;
    } else {
      combined = 'N/A';
    }
  } catch (e) {
    console.log('Rating extraction error:', e.message);
  }

  return { rating, reviews, combined };
}

for (const htmlStr of data) {
  if (typeof htmlStr === 'string') {
    const comp = {};

    // Extract name
    comp['ƒ∞sim'] = getText(htmlStr, /<a[^>]*class[^>]*ext[^>]*>([^<]+)</i);

    // Extract rating and reviews
    const ratingInfo = extractRatingAndReviews(htmlStr);
    comp['Puan'] = ratingInfo.rating;
    comp['Yorum Sayƒ±sƒ±'] = ratingInfo.reviews;
    comp['Puan/Yorum'] = ratingInfo.combined;

    // Extract address
    comp['Adres'] = getText(htmlStr, /<i[^>]*class[^>]*fa-map-marker[^>]*></i>[\s\S]*?<span[^>]*>([^<]+)</i);

    // Extract categories
    comp['Kategoriler'] = getText(htmlStr, /Categories:[^<]*<[^>]*>([^<]+)</i);

    // Extract website
    const websiteMatch = htmlStr.match(/<i[^>]*class[^>]*fa-globe[^>]*>[\s\S]*?<a[^>]*href="([^"]+)"/i);
    comp['Web Sitesi'] = websiteMatch ? websiteMatch[1] : 'N/A';

    // Extract photos count
    comp['Fotoƒüraf Sayƒ±sƒ±'] = getText(htmlStr, /<i[^>]*class[^>]*fa-photo[^>]*></i>[\s\S]*?<span[^>]*>([^<]+)</i);

    // Extract claim status
    const claimMatch = htmlStr.match(/((?:Un\s*)?Claimed)/i);
    comp['Sahiplenme Durumu'] = claimMatch ? claimMatch[1] : 'N/A';

    // Extract location count
    comp['Bulunduƒüu Konum Sayƒ±sƒ±'] = getText(htmlStr, /<td[^>]*class[^>]*text-center[^>]*><h5[^>]*>([^<]+)</i);

    // Extract average ranking
    comp['Ortalama Sƒ±ralama'] = getText(htmlStr, /<span[^>]*class[^>]*dotlg2[^>]*>([^<]+)</i);

    // Only add if we have a name
    if (comp['ƒ∞sim'] && comp['ƒ∞sim'] !== 'N/A') {
      competitors.push(comp);
    }
  }
}

return { competitors: competitors };

// Parse Sponsored Listings (equivalent to parse_sponsorlu_listeler)
