  let combined = 'N/A';

  try {
    // Extract rating
    const ratingMatch = htmlStr.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
    if (ratingMatch) {
      rating = ratingMatch[1].replace(',', '.');
    }

    // Extract reviews
    const reviewMatch = htmlStr.match(/\(?\s*([0-9]+)\s*(?:Reviews?|Yorum)?\s*\)?/i);
    if (reviewMatch) {
      reviews = reviewMatch[1];
    }

    // Create combined format
    if (rating !== 'N/A' && reviews !== '0') {
