for (const htmlStr of data) {
  if (typeof htmlStr === 'string') {
    const listing = {};

    // Extract name
    listing['ƒ∞sim'] = getText(htmlStr, /<a[^>]*class[^>]*ext[^>]*>([^<]+)</i);

    // Extract rating and reviews
    const ratingInfo = extractRatingAndReviews(htmlStr);
    listing['Puan'] = ratingInfo.rating;
    listing['Yorum Sayƒ±sƒ±'] = ratingInfo.reviews;
    listing['Puan/Yorum'] = ratingInfo.combined;

    // Extract appearance count
    const tdMatches = htmlStr.match(/<td[^>]*>([\s\S]*?)</td>/gi) || [];
    if (tdMatches.length > 0) {
      const lastTd = tdMatches[tdMatches.length - 1];
      const appearanceText = lastTd.replace(/<[^>]*>/g, '').trim();
      listing['G√∂r√ºlme Sayƒ±sƒ±'] = appearanceText || 'N/A';
    } else {
      listing['G√∂r√ºlme Sayƒ±sƒ±'] = 'N/A';
    }

    // Only add if we have a name
    if (listing['ƒ∞sim'] && listing['ƒ∞sim'] !== 'N/A') {
      listings.push(listing);
    }
  }
}

return { sponsored_listings: listings };

// Parse Detailed Results (equivalent to parse_detayli_sonuclar)
const data = $input.first().json.data || [];
const detaylar = [];

// Helper function to safely get text
function getText(htmlStr, pattern) {
  try {
    const match = htmlStr.match(pattern);
    return match ? match[1].trim() : 'N/A';
  } catch (e) {
    return 'N/A';
  }
}

// Helper function to extract rating and reviews
function extractRatingAndReviews(htmlStr) {
  let rating = 'N/A';
  let reviews = '0';
  let combined = 'N/A';

  try {
    const ratingMatch = htmlStr.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
    if (ratingMatch) {
      rating = ratingMatch[1].replace(',', '.');
    }

    const reviewMatch = htmlStr.match(/\(?\s*([0-9]+)\s*(?:Reviews?|Yorum)?\s*\)?/i);
    if (reviewMatch) {
      reviews = reviewMatch[1];
    }

    if (rating !== 'N/A' && reviews !== '0') {
      combined = `${rating} (${reviews})`;
    } else if (rating !== 'N/A') {
      combined = rating;
    }
  } catch (e) {
    console.log('Rating extraction error:', e.message);
  }

  return { rating, reviews, combined };
}

for (const htmlStr of data) {
  if (typeof htmlStr === 'string') {
    const detail = {};

    // Extract rank
    detail['Sƒ±ra'] = getText(htmlStr, /<span[^>]*class[^>]*dot[^>]*>([^<]+)</i);

    // Extract name
    detail['ƒ∞sim'] = getText(htmlStr, /<h5[^>]*>([^<]+)</i);

    // Extract rating and reviews
    const ratingInfo = extractRatingAndReviews(htmlStr);
    detail['Puan'] = ratingInfo.rating;
    detail['Yorum Sayƒ±sƒ±'] = ratingInfo.reviews;
    detail['Puan/Yorum'] = ratingInfo.combined;

    // Extract address (more complex extraction)
    const divMatches = htmlStr.match(/<div[^>]*>([\s\S]*?)</div>/gi) || [];
    let address = 'N/A';
    for (const div of divMatches) {
      const text = div.replace(/<[^>]*>/g, '').trim();
      if (text && text.length > 10 && !text.includes('rating') && !text.includes('Reviews')) {
        address = text;
        break;
      }
    }
    detail['Adres'] = address;

    // Only add if we have meaningful data
    if ((detail['Sƒ±ra'] && detail['Sƒ±ra'] !== 'N/A') || (detail['ƒ∞sim'] && detail['ƒ∞sim'] !== 'N/A')) {
      detaylar.push(detail);
    }
  }
}

return { detailed_results: detaylar };

// Combine All Data - Full Version (equivalent to MainScraper.scrape_all + DataExporter)
// This combines all parsed data into the final structure

const allInputs = $input.all();

// Extract data from all inputs
const jsData = allInputs.find(item => item.json.javascript_data)?.json.javascript_data || {};
const scanInfo = allInputs.find(item => item.json.scan_information)?.json.scan_information || {};
const rankSummary = allInputs.find(item => item.json.rank_summary)?.json.rank_summary || {};
const competitors = allInputs.find(item => item.json.competitors)?.json.competitors || [];
const sponsoredListings = allInputs.find(item => item.json.sponsored_listings)?.json.sponsored_listings || [];
const detailedResults = allInputs.find(item => item.json.detailed_results)?.json.detailed_results || [];
const mapDataFromJs = allInputs.find(item => item.json.map_data_from_js)?.json.map_data_from_js || [];
const analyticsData = allInputs.find(item => item.json.analytics_data)?.json || {};
const competitorsApiData = allInputs.find(item => item.json.competitors_api_data)?.json || {};
const htmlContent = allInputs.find(item => item.json.html_content)?.json.html_content || '';

// Combine scan information and rank summary (equivalent to ozet_bilgiler)
const ozetBilgiler = {
  ...scanInfo,
  ...rankSummary
};

// Enhanced map data combining JS and HTML sources
const enhancedMapData = [...mapDataFromJs];

// Enhanced API data structure
const enhancedApiData = {
  analytics_data: Array.isArray(analyticsData) ? analyticsData : [analyticsData].filter(Boolean),
  competitors_data: Array.isArray(competitorsApiData) ? competitorsApiData : [competitorsApiData].filter(Boolean),
  ranking_data: [],
  last_updated: new Date().toISOString()
};

// Enhanced JavaScript data with full validation
const enhancedJsData = {
  pinz: jsData.pinz_array || [],
  scan_guid: jsData.scan_guid || '',
  place_id: jsData.place_id || '',
  ...jsData
};

// Create metadata (equivalent to MainScraper metadata)
const metadata = {
  scraped_at: new Date().toISOString(),
  url: $('Fetch HTML - Primary').item?.json?.url || 'Unknown',
  scraper_version: '5.0-full',
  method: 'n8n_modular_hybrid_full',
  selenium_used: false, // Set to true if Selenium fallback was used
  total_competitors: competitors.length,
  total_sponsored: sponsoredListings.length,
  total_detailed_results: detailedResults.length,
  total_map_points: enhancedMapData.length,
  parsing_success: {
    js_data: Object.keys(jsData).length > 0,
    competitors: competitors.length > 0,
    sponsored: sponsoredListings.length > 0,
    summary: Object.keys(ozetBilgiler).length > 0,
    detailed_results: detailedResults.length > 0,
    api_data: Object.keys(enhancedApiData).length > 0
  },
  data_quality: {
    html_length: htmlContent.length,
    has_scan_guid: !!jsData.scan_guid,
    has_place_id: !!jsData.place_id,
    has_pinz_data: (jsData.pinz_array || []).length > 0
  }
};

// Final complete data structure (equivalent to Python main_scraper.py output)
const finalData = {
  ozet_bilgiler: ozetBilgiler,
  rakipler: competitors,
  sponsorlu_listeler: sponsoredListings,
  detayli_sonuclar: detailedResults,
  harita_verileri: enhancedMapData,
  api_verileri: enhancedApiData,
  javascript_verileri: enhancedJsData,
  metadata: metadata
};

return finalData;

