// Extract JavaScript data and prepare for API calls
const scriptsHtml = $input.first().json.all_scripts;
if (!scriptsHtml) return [];

const cheerio = require('cheerio');
const $ = cheerio.load(scriptsHtml);

let jsData = {
  pinz: [],
  scan_guid: null,
  place_id: null,
  base_url: ''
};

// Extract base URL from input
const inputUrl = $input.first().json.url || $('input').data('url');
if (inputUrl) {
  const urlObj = new URL(inputUrl);
  jsData.base_url = `${urlObj.protocol}//${urlObj.host}`;
}

$('script').each((i, script) => {
  const scriptText = $(script).html() || '';

  // Extract pinz array
  const pinzMatch = scriptText.match(/var\s+pinz\s*=\s*(\[.*?\]);/s);
  if (pinzMatch) {
    try {
      jsData.pinz = JSON.parse(pinzMatch[1]);
    } catch (e) {
      console.log('Error parsing pinz array:', e);
    }
  }

  // Extract scan_guid
  const scanGuidMatch = scriptText.match(/scan_guid['"]?\s*[:=]\s*['"]([^'"]+)['"]/i);
  if (scanGuidMatch) {
    jsData.scan_guid = scanGuidMatch[1];
  }

  // Extract place_id
  const placeIdMatch = scriptText.match(/place_id['"]?\s*[:=]\s*['"]([^'"]+)['"]/i);
  if (placeIdMatch) {
    jsData.place_id = placeIdMatch[1];
  }
});

return [{ json: { javascript_data: jsData, original_data: $input.first().json } }];

// Create multiple HTTP requests for analytics data
const jsData = $input.first().json.javascript_data;
const baseUrl = jsData.base_url;
const pinzArray = jsData.pinz || [];

const requests = [];

// Create API requests for each pin
pinzArray.slice(0, 10).forEach((pin, index) => { // Limit to first 10 for demo
  if (pin.url) {
    requests.push({
      json: {
        url: `${baseUrl}${pin.url}`,
        pin_index: index,
        pin_data: pin
      }
    });
  }
});

return requests;

// Parse basic HTML data
const originalData = $input.first().json.original_data;

const cheerio = require('cheerio');

// Parse Scan Information
let scanInfo = {};
if (originalData.scan_info_table) {
  const $ = cheerio.load(originalData.scan_info_table);
  $('tr').each((i, row) => {
    const cells = $(row).find('td');
    if (cells.length >= 2) {
      const key = $(cells[0]).text().trim().replace(':', '');
      const value = $(cells[1]).text().trim();
      scanInfo[key] = value;
    }
  });
}

// Parse Rank Summary
let rankSummary = {};
if (originalData.rank_summary_table) {
  const $ = cheerio.load(originalData.rank_summary_table);
  $('tr').each((i, row) => {
    const cells = $(row).find('td');
    if (cells.length >= 2) {
      const key = $(cells[0]).text().trim().replace(':', '');
      const value = $(cells[1]).text().trim();
      rankSummary[key] = value;
    }
  });
}

// Parse Competitors
let competitors = [];
if (originalData.competitors_table) {
  const $ = cheerio.load(originalData.competitors_table);
  $('tbody tr').each((i, row) => {
    const cells = $(row).find('td');
    if (cells.length >= 3) {
      competitors.push({
        rank: $(cells[0]).text().trim(),
        business_name: $(cells[1]).text().trim(),
        address: $(cells[2]).text().trim(),
        phone: cells.length > 3 ? $(cells[3]).text().trim() : 'N/A',
        website: cells.length > 4 ? $(cells[4]).text().trim() : 'N/A'
      });
    }
  });
}

return [{
  json: {
    scan_information: scanInfo,
    rank_summary: rankSummary,
    competitors: competitors
  }
}];

// Combine all data into final structure
const allInputs = $input.all();

let combinedData = {
  ozet_bilgiler: {},
  rakipler: [],
  sponsorlu_listeler: [],
  detayli_sonuclar: [],
  harita_verileri: [],
  javascript_verileri: {},
  api_verileri: {
    competitors: null,
    analytics: []
  },
  metadata: {
    scraped_at: new Date().toISOString(),
    scraper_version: '2.0-n8n-advanced',
    method: 'n8n_advanced_workflow_with_api'
  }
};

// Process each input
allInputs.forEach(input => {
  const data = input.json;

  // HTML parsed data
  if (data.scan_information) {
    combinedData.ozet_bilgiler = { ...combinedData.ozet_bilgiler, ...data.scan_information };
  }
  if (data.rank_summary) {
    combinedData.ozet_bilgiler = { ...combinedData.ozet_bilgiler, ...data.rank_summary };
  }
  if (data.competitors) {
    combinedData.rakipler = data.competitors;
  }

  // JavaScript data
  if (data.javascript_data) {
    combinedData.javascript_verileri = data.javascript_data;

    // Extract map data from pinz array
    if (data.javascript_data.pinz && Array.isArray(data.javascript_data.pinz)) {
      combinedData.harita_verileri = data.javascript_data.pinz.map(pin => ({
        lat: pin.lat,
        lng: pin.lng,
        title: pin.title || 'N/A',
        address: pin.address || 'N/A',
        url: pin.url || 'N/A'
      }));
    }
  }

  // API data from competitors endpoint
  if (data.data && Array.isArray(data.data)) {
    combinedData.api_verileri.competitors = data.data;
  }

  // API data from analytics endpoints
  if (data.pin_index !== undefined) {
    combinedData.api_verileri.analytics.push({
      pin_index: data.pin_index,
      pin_data: data.pin_data,
      api_response: data
    });
  }
});

return [{ json: combinedData }];

// Convert to CSV format for Excel compatibility
const data = $input.first().json;

// Create CSV for ozet_bilgiler
let csvContent = 'Category,Key,Value\n';
Object.entries(data.ozet_bilgiler || {}).forEach(([key, value]) => {
  csvContent += `"Ã–zet Bilgiler","${key}","${value}"\n`;
});

// Add competitors
(data.rakipler || []).forEach((competitor, index) => {
  csvContent += `"Rakipler","Rank ${competitor.rank}","${competitor.business_name} - ${competitor.address}"\n`;
});

// Add map data
(data.harita_verileri || []).slice(0, 10).forEach((location, index) => {
  csvContent += `"Harita Verileri","Location ${index + 1}","${location.title} (${location.lat}, ${location.lng})"\n`;
});

return [{ json: { csv_content: csvContent } }];

// Create execution summary
const data = $input.first().json;

const summary = {
  execution_time: new Date().toISOString(),
  data_summary: {
    ozet_bilgiler_count: Object.keys(data.ozet_bilgiler || {}).length,
    rakipler_count: (data.rakipler || []).length,
    harita_verileri_count: (data.harita_verileri || []).length,
    api_competitors_available: !!data.api_verileri?.competitors,
    api_analytics_count: (data.api_verileri?.analytics || []).length
  },
  status: 'completed',
  files_created: [
    `advanced_scraped_data_${new Date().toISOString().split('T')[0]}.json`,
    `advanced_scraped_data_${new Date().toISOString().split('T')[0]}.csv`
  ]
};

return [{ json: summary }];
