try {
  session.steps.excel_export = { status: 'in_progress', timestamp: new Date().toISOString() };

  // Create Excel-compatible data structure
  const excelData = {
    // Sheet 1: √ñzet Bilgiler (Summary Information)
    ozet_bilgiler: finalData.ozet_bilgiler && Object.keys(finalData.ozet_bilgiler).length > 0 ? 
      [finalData.ozet_bilgiler] : 
