    sponsorlu_listeler: finalData.sponsorlu_listeler.length > 0 ? 
      finalData.sponsorlu_listeler : 
      [{ message: 'No sponsored listings found' }],

    // Sheet 4: Detaylƒ± Sonu√ßlar (Detailed Results)
    detayli_sonuclar: finalData.detayli_sonuclar.length > 0 ? 
      finalData.detayli_sonuclar : 
      [{ message: 'No detailed results found' }],

    // Sheet 5: Harita Verileri (Map Data)
    harita_verileri: finalData.harita_verileri.length > 0 ? 
      finalData.harita_verileri : 
      [{ message: 'No map data found' }],

    // Sheet 6: JavaScript Verileri (JavaScript Data)
    javascript_verileri: finalData.javascript_verileri && Object.keys(finalData.javascript_verileri).length > 0 ? 
      [finalData.javascript_verileri] : 
      [{ message: 'No JavaScript data found' }],

    // Sheet 7: API Verileri (API Data)
    api_verileri: finalData.api_verileri && (finalData.api_verileri.competitors || finalData.api_verileri.analytics.length > 0) ? 
      [finalData.api_verileri] : 
      [{ message: 'No API data found' }],

    // Metadata sheet
    metadata: [finalData.metadata]
  };

  // Create CSV content for each sheet (Excel compatibility)
  const csvData = {};

  Object.keys(excelData).forEach(sheetName => {
    const sheetData = excelData[sheetName];
    let csvContent = '';

    if (sheetData.length > 0) {
      // Get all possible columns
      const allColumns = new Set();
      sheetData.forEach(row => {
        Object.keys(row).forEach(col => allColumns.add(col));
      });

      const columns = Array.from(allColumns);

      // Header row
      csvContent += columns.map(col => `"${col}"`).join(',') + '\n';

      // Data rows
      sheetData.forEach(row => {
        const values = columns.map(col => {
          const value = row[col] || '';
          if (typeof value === 'object') {
            return `"${JSON.stringify(value).replace(/"/g, '""')}"`;
          }
          return `"${String(value).replace(/"/g, '""')}"`;
        });
        csvContent += values.join(',') + '\n';
      });
    }

    csvData[sheetName] = csvContent;
  });

  session.steps.excel_export = { status: 'completed', timestamp: new Date().toISOString() };

  console.log('‚úÖ Excel Export Data Prepared');
  console.log(`üìä Created ${Object.keys(excelData).length} sheets`);
