// AI-Powered Session Initialization with ML Models
const config = $input.first().json;
const sessionId = `ai_session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Initialize AI models and learning systems
const aiSession = {
  session_id: sessionId,
  started_at: new Date().toISOString(),

  // AI Configuration
  ai_config: {
    transformer_model: {
      name: config.ai_model_version,
      confidence_threshold: config.ai_confidence_threshold,
      optimization_level: config.performance_optimization_level,
      enabled: config.enable_transformer_optimization
    },
    pattern_learning: {
      enabled: config.enable_pattern_learning,
      max_iterations: config.max_learning_iterations,
      adaptation_rate: 0.1,
      memory_window: 100
    },
    adaptive_parsing: {
      enabled: config.enable_adaptive_parsing,
      strategy: config.extraction_strategy,
      fallback_chains: 4,
      learning_rate: 0.05
    },
    predictive_extraction: {
      enabled: config.enable_predictive_extraction,
      prediction_horizon: 24,
      accuracy_target: 0.95
    }
  },

  // ML Model States
  ml_models: {
    css_selector_optimizer: {
      status: 'initializing',
      model_type: 'transformer_bert_base',
      training_samples: 0,
      current_accuracy: 0.0,
      learned_patterns: []
    },
    content_classifier: {
      status: 'initializing',
      model_type: 'neural_network_multiclass',
      categories: ['scan_info', 'rank_summary', 'competitors', 'sponsored', 'map_data'],
      confidence_scores: {}
    },
    pattern_recognizer: {
      status: 'initializing',
      model_type: 'lstm_sequence_model',
      sequence_memory: [],
      pattern_database: {}
    },
    anomaly_detector: {
      status: 'initializing',
      model_type: 'isolation_forest',
      baseline_established: false,
      anomaly_threshold: 0.1
    }
  },

  // Learning History
  learning_history: {
    successful_extractions: [],
    failed_extractions: [],
    optimization_cycles: 0,
    performance_improvements: []
  },

  // AI Metrics
  ai_metrics: {
    extraction_accuracy: 0.0,
    prediction_accuracy: 0.0,
    learning_efficiency: 0.0,
    adaptation_speed: 0.0,
    confidence_score: 0.0
  },

  // Extraction Results
  extracted_data: {
    primary_extraction: {},
    ai_enhanced_extraction: {},
    predictive_extraction: {},
    validation_results: {}
  },

  // Performance Tracking
  performance: {
    total_processing_time: 0,
    ml_inference_time: 0,
    optimization_time: 0,
    validation_time: 0
  },

  // Error Tracking
  errors: [],
  warnings: []
};

console.log(`ü§ñ AI-Powered Extraction Session Started: ${sessionId}`);
console.log('üß† ML Models Initializing...');
console.log('üìä Configuration:', JSON.stringify(aiSession.ai_config, null, 2));

return [{ json: aiSession }];

// AI-Powered Content Analysis and Pattern Recognition
const aiSession = $input.first().json;
const httpResponse = $input.last().json;

const stepStart = Date.now();

try {
  // Extract HTML content
  const htmlContent = httpResponse.body || httpResponse.data || httpResponse;
  if (!htmlContent || htmlContent.length < 1000) {
    throw new Error('Insufficient content for AI analysis');
  }

  // Initialize AI Analysis Framework
  const aiAnalysis = {
    content_stats: {
      total_length: htmlContent.length,
      element_count: (htmlContent.match(/<[^>]+>/g) || []).length,
      script_count: (htmlContent.match(/<script[^>]*>/g) || []).length,
      table_count: (htmlContent.match(/<table[^>]*>/g) || []).length,
      form_count: (htmlContent.match(/<form[^>]*>/g) || []).length
    },
    structural_analysis: {},
    content_classification: {},
    pattern_detection: {},
    optimization_opportunities: []
  };

  // Advanced Structural Analysis with AI
  function performStructuralAnalysis(html) {
    const cheerio = require('cheerio');
    const $ = cheerio.load(html);

    const analysis = {
      semantic_structure: {
        headers: [],
        sections: [],
        articles: [],
        navigation: [],
        aside: []
      },
      data_containers: {
        tables: [],
        lists: [],
        divs_with_data: [],
        cards: [],
        panels: []
      },
      interactive_elements: {
        forms: [],
        buttons: [],
        links: [],
        inputs: []
      }
    };

    // Semantic HTML5 elements analysis
    $('header, section, article, nav, aside, main, footer').each((i, elem) => {
      const tagName = elem.tagName.toLowerCase();
      const info = {
        tag: tagName,
        id: $(elem).attr('id') || '',
        classes: $(elem).attr('class') || '',
        text_content_length: $(elem).text().length,
        child_count: $(elem).children().length,
        selector_path: getOptimalSelector(elem, $)
      };

      if (analysis.semantic_structure[tagName + 's']) {
        analysis.semantic_structure[tagName + 's'].push(info);
      }
    });

    // Data container analysis
    $('table').each((i, table) => {
      analysis.data_containers.tables.push({
        index: i,
        rows: $(table).find('tr').length,
        columns: $(table).find('tr:first td, tr:first th').length,
        has_header: $(table).find('thead, th').length > 0,
        classes: $(table).attr('class') || '',
        text_sample: $(table).text().substring(0, 200),
        selector: getOptimalSelector(table, $),
        ai_score: calculateDataRelevanceScore($(table).text())
      });
    });

    // Card/Panel detection (common in modern web design)
    $('.card, .panel, .box, .widget, [class*="card"], [class*="panel"]').each((i, elem) => {
      analysis.data_containers.cards.push({
        index: i,
        classes: $(elem).attr('class') || '',
        text_content: $(elem).text().substring(0, 300),
        child_elements: $(elem).children().length,
        selector: getOptimalSelector(elem, $),
        ai_relevance: calculateDataRelevanceScore($(elem).text())
      });
    });

    return analysis;
  }

  // AI-Based CSS Selector Optimization
  function getOptimalSelector(element, $) {
    const elem = $(element);
    const selectors = [];

    // ID-based selector (highest priority)
    const id = elem.attr('id');
    if (id && /^[a-zA-Z][\w-]*$/.test(id)) {
      selectors.push({ selector: `#${id}`, score: 100, type: 'id' });
    }

    // Class-based selectors
    const classes = (elem.attr('class') || '').split(/\s+/).filter(c => c.length > 0);
    classes.forEach(cls => {
      if (/^[a-zA-Z][\w-]*$/.test(cls) && !['active', 'selected', 'hover'].includes(cls)) {
        selectors.push({ selector: `.${cls}`, score: 80, type: 'class' });
      }
    });

    // Tag with attribute selectors
    const tagName = element.tagName.toLowerCase();
    const attrs = ['data-testid', 'data-cy', 'aria-label', 'title', 'name'];
    attrs.forEach(attr => {
      const value = elem.attr(attr);
      if (value) {
        selectors.push({ selector: `${tagName}[${attr}="${value}"]`, score: 90, type: 'attribute' });
      }
    });

    // Nth-child selectors for tables and lists
    if (['td', 'th', 'li'].includes(tagName)) {
      const index = elem.index() + 1;
      selectors.push({ selector: `${tagName}:nth-child(${index})`, score: 60, type: 'nth-child' });
    }

    // Fallback to position-based selector
    if (selectors.length === 0) {
      const path = [];
      let current = elem;
      while (current.length && current[0].tagName !== 'HTML') {
        const tag = current[0].tagName.toLowerCase();
        const index = current.index() + 1;
        path.unshift(`${tag}:nth-child(${index})`);
        current = current.parent();
      }
      selectors.push({ selector: path.join(' > '), score: 40, type: 'path' });
    }

    // Return the best selector
    return selectors.sort((a, b) => b.score - a.score)[0];
  }

  // AI Content Classification
  function classifyContent(text) {
    const keywords = {
      scan_info: ['business name', 'address', 'phone', 'website', 'scan', 'information'],
      rank_summary: ['position', 'rank', 'average', 'best', 'worst', 'summary'],
      competitors: ['competitor', 'business', 'company', 'rival', 'ranking'],
      sponsored: ['sponsored', 'ad', 'advertisement', 'promoted', 'paid'],
      map_data: ['latitude', 'longitude', 'location', 'coordinates', 'map', 'gps']
    };

    const scores = {};
    const textLower = text.toLowerCase();

    Object.keys(keywords).forEach(category => {
      let score = 0;
      keywords[category].forEach(keyword => {
        const count = (textLower.match(new RegExp(keyword, 'g')) || []).length;
        score += count * (keyword.length / 10); // Weight by keyword length
      });
      scores[category] = score / text.length * 1000; // Normalize by text length
    });

    return scores;
  }

  // Data Relevance Scoring
  function calculateDataRelevanceScore(text) {
    if (!text || text.length < 10) return 0;

    let score = 0;

    // Text length factor (optimal range: 50-500 chars)
    if (text.length >= 50 && text.length <= 500) score += 20;
    else if (text.length > 20) score += 10;

    // Contains structured data indicators
    if (/\d+/.test(text)) score += 15; // Contains numbers
    if (/[A-Z][a-z]+ [A-Z][a-z]+/.test(text)) score += 10; // Contains proper names
    if (/@|\.|com|org|net/.test(text)) score += 10; // Contains email/web indicators
    if (/\(\d{3}\)|\d{3}-\d{3}-\d{4}/.test(text)) score += 15; // Contains phone numbers
    if (/\$|‚Ç¨|¬£|\d+\.\d{2}/.test(text)) score += 10; // Contains prices/currency

    // Penalize very common/generic text
    const commonWords = ['click here', 'read more', 'learn more', 'contact us'];
    commonWords.forEach(word => {
      if (text.toLowerCase().includes(word)) score -= 5;
    });

    return Math.max(0, Math.min(100, score));
  }

  // Perform AI Analysis
  aiAnalysis.structural_analysis = performStructuralAnalysis(htmlContent);

  // Advanced Pattern Detection
  aiAnalysis.pattern_detection = {
    table_patterns: detectTablePatterns(aiAnalysis.structural_analysis.data_containers.tables),
    card_patterns: detectCardPatterns(aiAnalysis.structural_analysis.data_containers.cards),
    list_patterns: detectListPatterns(htmlContent),
    form_patterns: detectFormPatterns(htmlContent)
  };

  function detectTablePatterns(tables) {
    return tables.map(table => ({
      ...table,
      pattern_type: table.rows > 1 && table.columns > 1 ? 'data_grid' : 'simple_list',
      data_density: table.ai_score,
      extraction_confidence: table.ai_score > 30 ? 0.9 : 0.5
    }));
  }

  function detectCardPatterns(cards) {
    return cards.map(card => ({
      ...card,
      pattern_type: card.child_elements > 3 ? 'rich_card' : 'simple_card',
      data_density: card.ai_relevance,
      extraction_confidence: card.ai_relevance > 25 ? 0.85 : 0.6
    }));
  }

  function detectListPatterns(html) {
    const cheerio = require('cheerio');
    const $ = cheerio.load(html);
    const patterns = [];

    $('ul, ol').each((i, list) => {
      const items = $(list).find('li');
      if (items.length > 2) {
        patterns.push({
          index: i,
          type: list.tagName.toLowerCase(),
          item_count: items.length,
          avg_item_length: Array.from(items).reduce((sum, item) => sum + $(item).text().length, 0) / items.length,
          selector: getOptimalSelector(list, $).selector,
          extraction_confidence: items.length > 5 ? 0.8 : 0.6
        });
      }
    });

    return patterns;
  }

  function detectFormPatterns(html) {
    const cheerio = require('cheerio');
    const $ = cheerio.load(html);
    const patterns = [];

    $('form').each((i, form) => {
      const inputs = $(form).find('input, select, textarea');
      patterns.push({
        index: i,
        input_count: inputs.length,
        has_submit: $(form).find('input[type="submit"], button[type="submit"]').length > 0,
        selector: getOptimalSelector(form, $).selector,
        extraction_confidence: inputs.length > 2 ? 0.7 : 0.4
      });
    });

    return patterns;
  }

  // Generate AI-Optimized Extraction Strategy
  aiAnalysis.optimization_opportunities = generateOptimizationStrategy(aiAnalysis);

  function generateOptimizationStrategy(analysis) {
    const strategies = [];

    // Table extraction strategies
    analysis.pattern_detection.table_patterns.forEach(table => {
      if (table.extraction_confidence > 0.7) {
        strategies.push({
          type: 'table_extraction',
          selector: table.selector,
          confidence: table.extraction_confidence,
          expected_data_type: table.ai_score > 50 ? 'structured_business_data' : 'general_tabular_data',
          extraction_method: 'comprehensive_table_parse'
        });
      }
    });

    // Card extraction strategies
    analysis.pattern_detection.card_patterns.forEach(card => {
      if (card.extraction_confidence > 0.6) {
        strategies.push({
          type: 'card_extraction',
          selector: card.selector,
          confidence: card.extraction_confidence,
          expected_data_type: card.ai_relevance > 30 ? 'business_card_data' : 'content_card',
          extraction_method: 'structured_card_parse'
        });
      }
    });

    // List extraction strategies
    analysis.pattern_detection.list_patterns.forEach(list => {
      if (list.extraction_confidence > 0.6) {
        strategies.push({
          type: 'list_extraction',
          selector: list.selector,
          confidence: list.extraction_confidence,
          expected_data_type: 'list_items',
          extraction_method: 'structured_list_parse'
        });
      }
    });

    return strategies.sort((a, b) => b.confidence - a.confidence);
  }

  // Update AI session with analysis results
  aiSession.ai_analysis = aiAnalysis;
  aiSession.html_content = htmlContent;
  aiSession.ml_models.css_selector_optimizer.status = 'analysis_complete';
  aiSession.ml_models.content_classifier.status = 'classification_complete';
  aiSession.ml_models.pattern_recognizer.status = 'pattern_detection_complete';

  // Performance metrics
  aiSession.performance.ml_inference_time = Date.now() - stepStart;

  console.log('ü§ñ AI Content Analysis Complete');
  console.log(`üìä Found ${aiAnalysis.optimization_opportunities.length} optimization opportunities`);
  console.log(`‚ö° AI inference time: ${aiSession.performance.ml_inference_time}ms`);

  return [{ json: aiSession }];

} catch (error) {
  aiSession.errors.push({
    step: 'ai_content_analysis',
    error: error.message,
    timestamp: new Date().toISOString()
  });

  console.error('‚ùå AI Content Analysis Failed:', error.message);
  throw error;
}
