// Initialize scraper session with comprehensive logging
const config = $input.first().json;
const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

const session = {
  session_id: sessionId,
  started_at: new Date().toISOString(),
  config: {
    target_url: config.target_url,
    user_agent: config.user_agent,
    timeout_seconds: config.timeout_seconds,
    retry_count: config.retry_count,
    rate_limit_ms: config.rate_limit_ms,
    enable_api_calls: config.enable_api_calls,
    save_raw_html: config.save_raw_html,
    enable_detailed_logging: config.enable_detailed_logging
  },
  steps: {
    initialization: {
      status: 'completed',
      timestamp: new Date().toISOString(),
      details: 'Session initialized successfully'
    },
    http_request: { status: 'pending' },
    html_parsing: { status: 'pending' },
    js_extraction: { status: 'pending' },
    api_calls: { status: 'pending' },
    data_assembly: { status: 'pending' },
    export: { status: 'pending' }
  },
  scraped_data: {
    scan_information: {},
    rank_summary: {},
    competitors: [],
    sponsored_listings: [],
    detailed_results: [],
    map_data: [],
    javascript_data: {},
    api_data: {}
  },
  metrics: {
    total_elements_found: 0,
    total_api_calls: 0,
    total_execution_time_ms: 0,
    data_quality_score: 0
  },
  errors: [],
  warnings: []
};

if (config.enable_detailed_logging) {
  console.log(`üöÄ Scraper session started: ${sessionId}`);
  console.log('üìã Configuration:', JSON.stringify(session.config, null, 2));
}

return [{ json: session }];

// Comprehensive HTTP response validation and processing
const session = $input.first().json;
const httpResponse = $input.last().json;

const stepStart = Date.now();
session.steps.http_request.timestamp = new Date().toISOString();

try {
  // Validate HTTP response
  if (!httpResponse) {
    throw new Error('No HTTP response received');
  }

  // Check status code
  const statusCode = httpResponse.statusCode || 200;
  if (statusCode >= 400) {
    throw new Error(`HTTP ${statusCode}: ${httpResponse.statusMessage || 'Request failed'}`);
  }

  // Extract response body
  let htmlContent = httpResponse.body || httpResponse.data || httpResponse;
  if (typeof htmlContent !== 'string') {
    if (typeof htmlContent === 'object' && htmlContent.toString) {
      htmlContent = htmlContent.toString();
    } else {
      throw new Error('Invalid response format - expected HTML string');
    }
  }

  // Validate HTML content
  if (htmlContent.length < 1000) {
    throw new Error(`Response too short (${htmlContent.length} chars) - possible error page`);
  }

  // Check for common error indicators
  const errorIndicators = [
    '404 Not Found',
    '403 Forbidden',
    '500 Internal Server Error',
    'Access Denied',
    'Page Not Found',
    'captcha',
    'robot',
    'blocked',
    'rate limit',
    'too many requests'
  ];

  const foundErrors = errorIndicators.filter(indicator => 
    htmlContent.toLowerCase().includes(indicator.toLowerCase())
  );

  if (foundErrors.length > 0) {
    throw new Error(`Error indicators found: ${foundErrors.join(', ')}`);
  }

  // Validate HTML structure
  const hasHtmlTag = htmlContent.includes('<html');
  const hasBodyTag = htmlContent.includes('<body');
  const hasScriptTags = htmlContent.includes('<script');
  const hasTables = htmlContent.includes('<table');

  if (!hasHtmlTag || !hasBodyTag) {
    session.warnings.push('HTML structure seems incomplete - missing html or body tags');
  }

  // Success metrics
  session.steps.http_request.status = 'completed';
  session.steps.http_request.details = {
    status_code: statusCode,
    content_length: htmlContent.length,
    has_html_structure: hasHtmlTag && hasBodyTag,
    has_script_tags: hasScriptTags,
    has_tables: hasTables,
    response_headers: httpResponse.headers || {},
    execution_time_ms: Date.now() - stepStart
  };

  // Add raw HTML to session if enabled
  if (session.config.save_raw_html) {
    session.raw_html = htmlContent;
  }

  if (session.config.enable_detailed_logging) {
    console.log('‚úÖ HTTP request completed successfully');
    console.log(`üìä Response size: ${htmlContent.length} bytes`);
    console.log(`‚è±Ô∏è Execution time: ${Date.now() - stepStart}ms`);
  }

  return [{ json: { ...session, html_content: htmlContent } }];

} catch (error) {
  session.steps.http_request.status = 'failed';
  session.steps.http_request.details = {
    error: error.message,
    execution_time_ms: Date.now() - stepStart
  };
  session.errors.push({
    step: 'http_request',
    error: error.message,
    timestamp: new Date().toISOString()
  });

  console.error('‚ùå HTTP request failed:', error.message);
  throw error;
}

// Comprehensive HTML parsing with intelligent fallbacks
const session = $input.first().json;
const extractedHtml = $input.last().json;

const stepStart = Date.now();
session.steps.html_parsing.timestamp = new Date().toISOString();

const cheerio = require('cheerio');

try {
  // Parse scan information with multiple fallback strategies
  function parseScanInformation(htmlElements) {
    const scanInfo = {};
    let parsed = false;

    // Strategy 1: Direct table parsing
    if (htmlElements.scan_info_table) {
      const $ = cheerio.load(htmlElements.scan_info_table);
      $('tr').each((i, row) => {
        const cells = $(row).find('td, th');
        if (cells.length >= 2) {
          const key = $(cells[0]).text().trim().replace(/[:\s]+$/, '');
          const value = $(cells[1]).text().trim();
          if (key && value) {
            scanInfo[key] = value;
            parsed = true;
          }
        }
      });
    }

    // Strategy 2: Parse all tables if scan_info_table not found
    if (!parsed && htmlElements.all_tables) {
      const $ = cheerio.load(htmlElements.all_tables);
      $('table').each((tableIndex, table) => {
        const tableText = $(table).text().toLowerCase();
        if (tableText.includes('business') || tableText.includes('address') || tableText.includes('phone')) {
          $(table).find('tr').each((i, row) => {
            const cells = $(row).find('td, th');
            if (cells.length >= 2) {
              const key = $(cells[0]).text().trim().replace(/[:\s]+$/, '');
              const value = $(cells[1]).text().trim();
              if (key && value && key.length < 50) {
                scanInfo[key] = value;
                parsed = true;
              }
            }
          });
        }
      });
    }

    // Strategy 3: Card-based parsing
    if (!parsed && htmlElements.card_sections) {
      const $ = cheerio.load(htmlElements.card_sections);
      $('.card, .card-body, .info-card').each((i, card) => {
        const cardText = $(card).text();
        if (cardText.toLowerCase().includes('scan') || cardText.toLowerCase().includes('business')) {
          $(card).find('table tr, .info-row, .data-row').each((j, row) => {
            const cells = $(row).find('td, th, .label, .value');
            if (cells.length >= 2) {
              const key = $(cells[0]).text().trim().replace(/[:\s]+$/, '');
              const value = $(cells[1]).text().trim();
              if (key && value) {
                scanInfo[key] = value;
                parsed = true;
              }
            }
          });
        }
      });
    }

    return { data: scanInfo, success: parsed };
  }

  // Parse rank summary with multiple strategies
  function parseRankSummary(htmlElements) {
    const rankSummary = {};
    let parsed = false;

    if (htmlElements.rank_summary_table) {
      const $ = cheerio.load(htmlElements.rank_summary_table);
      $('tr').each((i, row) => {
        const cells = $(row).find('td, th');
        if (cells.length >= 2) {
          const key = $(cells[0]).text().trim().replace(/[:\s]+$/, '');
          const value = $(cells[1]).text().trim();
          if (key && value) {
            rankSummary[key] = value;
            parsed = true;
          }
        }
      });
    }

    // Fallback: Look for rank-related data in all tables
    if (!parsed && htmlElements.all_tables) {
      const $ = cheerio.load(htmlElements.all_tables);
      $('table').each((tableIndex, table) => {
        const tableText = $(table).text().toLowerCase();
        if (tableText.includes('position') || tableText.includes('rank') || tableText.includes('average')) {
          $(table).find('tr').each((i, row) => {
            const cells = $(row).find('td, th');
            if (cells.length >= 2) {
              const key = $(cells[0]).text().trim().replace(/[:\s]+$/, '');
              const value = $(cells[1]).text().trim();
              if (key && value && (key.toLowerCase().includes('position') || key.toLowerCase().includes('rank'))) {
                rankSummary[key] = value;
                parsed = true;
              }
            }
          });
        }
      });
    }

    return { data: rankSummary, success: parsed };
  }

  // Parse competitors with advanced extraction
  function parseCompetitors(htmlElements) {
    const competitors = [];
    let parsed = false;

    if (htmlElements.competitors_table) {
      const $ = cheerio.load(htmlElements.competitors_table);

      // Find the table with competitor data
      $('table').each((tableIndex, table) => {
        const headers = [];
        $(table).find('thead tr th, tr:first-child td, tr:first-child th').each((i, header) => {
          headers.push($(header).text().trim().toLowerCase());
        });

        // Check if this looks like a competitors table
        const hasRankColumn = headers.some(h => h.includes('rank') || h.includes('#'));
        const hasBusinessColumn = headers.some(h => h.includes('business') || h.includes('name') || h.includes('company'));

        if (hasRankColumn || hasBusinessColumn || headers.length >= 3) {
          $(table).find('tbody tr, tr').each((i, row) => {
            if (i === 0 && $(row).find('th').length > 0) return; // Skip header row

            const cells = $(row).find('td, th');
            if (cells.length >= 2) {
              const competitor = {
                rank: $(cells[0]).text().trim() || (i + 1).toString(),
                business_name: $(cells[1]).text().trim() || 'N/A',
                address: cells.length > 2 ? $(cells[2]).text().trim() : 'N/A',
                phone: cells.length > 3 ? $(cells[3]).text().trim() : 'N/A',
                website: cells.length > 4 ? $(cells[4]).text().trim() : 'N/A',
                additional_info: cells.length > 5 ? $(cells[5]).text().trim() : 'N/A'
              };

              if (competitor.business_name !== 'N/A' && competitor.business_name.length > 0) {
                competitors.push(competitor);
                parsed = true;
              }
            }
          });
        }
      });
    }

    // Fallback: Search all tables for competitor-like data
    if (!parsed && htmlElements.all_tables) {
      const $ = cheerio.load(htmlElements.all_tables);
      $('table').each((tableIndex, table) => {
        const tableText = $(table).text().toLowerCase();
        if (tableText.includes('competitor') || tableText.includes('business') || tableText.includes('company')) {
          $(table).find('tr').each((i, row) => {
            const cells = $(row).find('td');
            if (cells.length >= 2) {
              const firstCell = $(cells[0]).text().trim();
              const secondCell = $(cells[1]).text().trim();

              // Check if this looks like competitor data
              if (firstCell.match(/^\d+$/) || secondCell.length > 5) {
                competitors.push({
                  rank: firstCell.match(/^\d+$/) ? firstCell : (i + 1).toString(),
                  business_name: firstCell.match(/^\d+$/) ? secondCell : firstCell,
                  address: cells.length > 2 ? $(cells[2]).text().trim() : 'N/A'
                });
                parsed = true;
              }
            }
          });
        }
      });
    }

    return { data: competitors.slice(0, 50), success: parsed }; // Limit to 50 competitors
  }

  // Parse sponsored listings
  function parseSponsoredListings(htmlElements) {
    const sponsoredListings = [];
    let parsed = false;

    if (htmlElements.sponsored_listings_section) {
      const $ = cheerio.load(htmlElements.sponsored_listings_section);

      $('div, li, .listing, .ad').each((i, element) => {
        const text = $(element).text().trim();
        const title = $(element).find('h1, h2, h3, h4, .title, .name').first().text().trim();
        const description = $(element).find('p, .description, .summary').first().text().trim();

        if (title || (text.length > 10 && text.length < 500)) {
          sponsoredListings.push({
            title: title || text.substring(0, 100),
            description: description || text,
            position: i + 1
          });
          parsed = true;
        }
      });
    }

    return { data: sponsoredListings, success: parsed };
  }

  // Execute all parsing functions
  const scanInfoResult = parseScanInformation(extractedHtml);
  const rankSummaryResult = parseRankSummary(extractedHtml);
  const competitorsResult = parseCompetitors(extractedHtml);
  const sponsoredResult = parseSponsoredListings(extractedHtml);

  // Update session data
  session.scraped_data.scan_information = scanInfoResult.data;
  session.scraped_data.rank_summary = rankSummaryResult.data;
  session.scraped_data.competitors = competitorsResult.data;
  session.scraped_data.sponsored_listings = sponsoredResult.data;

  // Calculate metrics
  session.metrics.total_elements_found = 
    Object.keys(scanInfoResult.data).length +
    Object.keys(rankSummaryResult.data).length +
    competitorsResult.data.length +
    sponsoredResult.data.length;

  // Update step status
  session.steps.html_parsing.status = 'completed';
  session.steps.html_parsing.details = {
    scan_info_parsed: scanInfoResult.success,
    scan_info_fields: Object.keys(scanInfoResult.data).length,
    rank_summary_parsed: rankSummaryResult.success,
    rank_summary_fields: Object.keys(rankSummaryResult.data).length,
    competitors_parsed: competitorsResult.success,
    competitors_count: competitorsResult.data.length,
    sponsored_parsed: sponsoredResult.success,
    sponsored_count: sponsoredResult.data.length,
    execution_time_ms: Date.now() - stepStart
  };

  // Add warnings for failed parsing
  if (!scanInfoResult.success) {
    session.warnings.push('Scan information parsing failed - using fallback extraction');
  }
  if (!rankSummaryResult.success) {
    session.warnings.push('Rank summary parsing failed - using fallback extraction');
  }
  if (!competitorsResult.success) {
    session.warnings.push('Competitors parsing failed - using fallback extraction');
  }

  if (session.config.enable_detailed_logging) {
    console.log('‚úÖ HTML parsing completed');
    console.log(`üìä Elements found: ${session.metrics.total_elements_found}`);
    console.log(`‚è±Ô∏è Execution time: ${Date.now() - stepStart}ms`);
  }

  return [{ json: { ...session, extracted_html: extractedHtml } }];

} catch (error) {
  session.steps.html_parsing.status = 'failed';
  session.steps.html_parsing.details = {
    error: error.message,
    execution_time_ms: Date.now() - stepStart
  };
  session.errors.push({
    step: 'html_parsing',
    error: error.message,
    timestamp: new Date().toISOString()
  });

  console.error('‚ùå HTML parsing failed:', error.message);
  throw error;
}

// Advanced JavaScript data extraction with multiple regex patterns
const session = $input.first().json;
const extractedHtml = session.extracted_html;

const stepStart = Date.now();
session.steps.js_extraction.timestamp = new Date().toISOString();

const cheerio = require('cheerio');

try {
  const jsData = {
    pinz: [],
    scan_guid: null,
    place_id: null,
    business_id: null,
    search_id: null,
    map_center: null,
    api_endpoints: [],
    raw_js_variables: {}
  };

  if (!extractedHtml.all_scripts) {
    throw new Error('No script tags found in HTML');
  }

  const $ = cheerio.load(extractedHtml.all_scripts);
  let scriptsProcessed = 0;
  let dataExtractionSuccess = false;

  $('script').each((scriptIndex, script) => {
    scriptsProcessed++;
    const scriptContent = $(script).html() || $(script).text() || '';

    if (!scriptContent || scriptContent.length < 10) return;

    // Extract pinz array with multiple patterns
    const pinzPatterns = [
      /var\s+pinz\s*=\s*(\[.*?\]);/gs,
      /pinz\s*=\s*(\[.*?\]);/gs,
      /['"]pinz['"]\s*:\s*(\[.*?\])/gs,
      /pinz['"]?\s*[:=]\s*(\[.*?\])/gs
    ];

    for (const pattern of pinzPatterns) {
      const pinzMatch = scriptContent.match(pattern);
      if (pinzMatch && pinzMatch[1]) {
        try {
          const pinzArray = JSON.parse(pinzMatch[1]);
          if (Array.isArray(pinzArray) && pinzArray.length > 0) {
            jsData.pinz = pinzArray;
            dataExtractionSuccess = true;
            break;
          }
        } catch (e) {
          // Try to fix common JSON issues
          try {
            const fixedJson = pinzMatch[1]
              .replace(/'/g, '"')
              .replace(/([{,])(\w+):/g, '$1"$2":')
              .replace(/:\s*([^,}\]]+)([,}\]])/g, ':"$1"$2')
              .replace(/:\s*""/g, ':null')
              .replace(/,\s*}/g, '}')
              .replace(/,\s*\]/g, ']');
            const pinzArray = JSON.parse(fixedJson);
            if (Array.isArray(pinzArray)) {
              jsData.pinz = pinzArray;
              dataExtractionSuccess = true;
              break;
            }
          } catch (e2) {
            console.log(`Failed to parse pinz array: ${e2.message}`);
          }
        }
      }
    }

    // Extract scan_guid with multiple patterns
    const scanGuidPatterns = [
      /scan_guid['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi,
      /['"]scan_guid['"]\s*:\s*['"]([^'"]+)['"]/gi,
      /scanGuid['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi,
      /scan[_-]?guid['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi
    ];

    for (const pattern of scanGuidPatterns) {
      const match = pattern.exec(scriptContent);
      if (match && match[1] && match[1].length > 10) {
        jsData.scan_guid = match[1];
        dataExtractionSuccess = true;
        break;
      }
    }

    // Extract place_id
    const placeIdPatterns = [
      /place_id['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi,
      /placeId['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi,
      /['"]place_id['"]\s*:\s*['"]([^'"]+)['"]/gi
    ];

    for (const pattern of placeIdPatterns) {
      const match = pattern.exec(scriptContent);
      if (match && match[1]) {
        jsData.place_id = match[1];
        break;
      }
    }

    // Extract business_id
    const businessIdPatterns = [
      /business_id['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi,
      /businessId['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi
    ];

    for (const pattern of businessIdPatterns) {
      const match = pattern.exec(scriptContent);
      if (match && match[1]) {
        jsData.business_id = match[1];
        break;
      }
    }

    // Extract search_id
    const searchIdPatterns = [
      /search_id['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi,
      /searchId['"]?\s*[:=]\s*['"]([^'"]+)['"]/gi
    ];

    for (const pattern of searchIdPatterns) {
      const match = pattern.exec(scriptContent);
      if (match && match[1]) {
        jsData.search_id = match[1];
        break;
      }
    }

    // Extract map center coordinates
    const mapCenterPatterns = [
      /map[_-]?center['"]?\s*[:=]\s*\{[^}]*lat['"]?\s*:\s*([\d.-]+)[^}]*lng['"]?\s*:\s*([\d.-]+)/gi,
      /center['"]?\s*[:=]\s*\{[^}]*lat['"]?\s*:\s*([\d.-]+)[^}]*lng['"]?\s*:\s*([\d.-]+)/gi,
      /lat['"]?\s*:\s*([\d.-]+)[^,}]*lng['"]?\s*:\s*([\d.-]+)/gi
    ];

    for (const pattern of mapCenterPatterns) {
      const match = pattern.exec(scriptContent);
      if (match && match[1] && match[2]) {
        jsData.map_center = {
          lat: parseFloat(match[1]),
          lng: parseFloat(match[2])
        };
        break;
      }
    }

    // Extract API endpoints
    const apiPatterns = [
      /['"]([^'"]*\/api\/[^'"]*)['"]|['"]([^'"]*\/analytics\/[^'"]*)['"]|['"]([^'"]*\/scans\/[^'"]*)['"]|['"]([^'"]*\/competitors[^'"]*)['"]|['"]([^'"]*\/results[^'"]*)['"]|['"]([^'"]*\/data[^'"]*)['"]|['"]([^'"]*\/search[^'"]*)['"]/gi
    ];

    let apiMatch;
    while ((apiMatch = apiPatterns.exec(scriptContent)) !== null) {
      const url = apiMatch[1] || apiMatch[2] || apiMatch[3] || apiMatch[4] || apiMatch[5] || apiMatch[6] || apiMatch[7];
      if (url && url.length > 5 && !jsData.api_endpoints.includes(url)) {
        jsData.api_endpoints.push(url);
      }
    }

    // Extract common JavaScript variables
    const variablePatterns = [
      /var\s+(\w+)\s*=\s*(['"][^'"]*['"]|\d+|true|false|null)/gi,
      /const\s+(\w+)\s*=\s*(['"][^'"]*['"]|\d+|true|false|null)/gi,
      /let\s+(\w+)\s*=\s*(['"][^'"]*['"]|\d+|true|false|null)/gi
    ];

    for (const pattern of variablePatterns) {
      let varMatch;
      while ((varMatch = pattern.exec(scriptContent)) !== null) {
        const varName = varMatch[1];
        const varValue = varMatch[2];
        if (varName && varValue && varName.length < 50) {
          jsData.raw_js_variables[varName] = varValue;
        }
      }
    }
  });

  // Validate extracted data
  if (!dataExtractionSuccess) {
    session.warnings.push('JavaScript data extraction failed - no critical data found');
  }

  // Process pinz data for map information
  if (jsData.pinz && jsData.pinz.length > 0) {
    session.scraped_data.map_data = jsData.pinz.map((pin, index) => ({
      index: index,
      lat: pin.lat || pin.latitude || 0,
      lng: pin.lng || pin.longitude || 0,
      title: pin.title || pin.name || pin.business_name || `Location ${index + 1}`,
      address: pin.address || pin.location || 'N/A',
      url: pin.url || pin.api_url || pin.endpoint || 'N/A',
      rank: pin.rank || pin.position || index + 1,
      distance: pin.distance || 'N/A',
      additional_data: {
        phone: pin.phone || pin.tel || 'N/A',
        website: pin.website || pin.web || 'N/A',
        rating: pin.rating || pin.stars || 'N/A',
        reviews: pin.reviews || pin.review_count || 'N/A'
      }
    }));
  }

  // Update session
  session.scraped_data.javascript_data = jsData;
  session.steps.js_extraction.status = 'completed';
  session.steps.js_extraction.details = {
    scripts_processed: scriptsProcessed,
    pinz_count: jsData.pinz.length,
    scan_guid_found: !!jsData.scan_guid,
    place_id_found: !!jsData.place_id,
    business_id_found: !!jsData.business_id,
    search_id_found: !!jsData.search_id,
    map_center_found: !!jsData.map_center,
    api_endpoints_found: jsData.api_endpoints.length,
    js_variables_found: Object.keys(jsData.raw_js_variables).length,
    execution_time_ms: Date.now() - stepStart
  };

  if (session.config.enable_detailed_logging) {
    console.log('‚úÖ JavaScript extraction completed');
    console.log(`üìä Pinz data: ${jsData.pinz.length} locations`);
    console.log(`üîë Scan GUID: ${jsData.scan_guid ? 'Found' : 'Not found'}`);
    console.log(`üåç Map center: ${jsData.map_center ? 'Found' : 'Not found'}`);
    console.log(`üîó API endpoints: ${jsData.api_endpoints.length}`);
    console.log(`‚è±Ô∏è Execution time: ${Date.now() - stepStart}ms`);
  }

  return [{ json: session }];

} catch (error) {
  session.steps.js_extraction.status = 'failed';
  session.steps.js_extraction.details = {
    error: error.message,
    execution_time_ms: Date.now() - stepStart
  };
  session.errors.push({
    step: 'js_extraction',
    error: error.message,
    timestamp: new Date().toISOString()
  });

  console.error('‚ùå JavaScript extraction failed:', error.message);
  throw error;
}

// Skip API calls and continue with data assembly
const session = $input.first().json;

