// Process API response and prepare for additional API calls
const session = $input.first().json;
const apiResponse = $input.last().json;

const stepStart = Date.now();
session.steps.api_calls = {
  timestamp: new Date().toISOString(),
  status: 'in_progress'
};

session.metrics.total_api_calls = 1;

try {
  // Process competitors API response
  let competitorsData = null;
  if (apiResponse && apiResponse.statusCode && apiResponse.statusCode < 400) {
    const responseBody = apiResponse.body || apiResponse.data || apiResponse;

    if (typeof responseBody === 'string') {
      try {
        competitorsData = JSON.parse(responseBody);
      } catch (e) {
        session.warnings.push(`Failed to parse competitors API response: ${e.message}`);
      }
    } else if (typeof responseBody === 'object') {
      competitorsData = responseBody;
    }
  } else {
    session.warnings.push(`Competitors API failed with status: ${apiResponse?.statusCode || 'unknown'}`);
  }

  // Initialize API data
  session.scraped_data.api_data = {
    competitors: competitorsData,
    analytics: [],
    additional_endpoints: [],
    status: 'partial'
  };

  // Prepare analytics API calls from pinz data
  const jsData = session.scraped_data.javascript_data;
  const baseUrl = session.config.target_url.split('/scan/')[0];
  const analyticsRequests = [];

  if (jsData.pinz && jsData.pinz.length > 0) {
    // Limit to first 10 locations for performance
    const limitedPinz = jsData.pinz.slice(0, 10);

    limitedPinz.forEach((pin, index) => {
      if (pin.url) {
        const fullUrl = pin.url.startsWith('/') ? baseUrl + pin.url : pin.url;
        analyticsRequests.push({
          url: fullUrl,
          pin_index: index,
          pin_data: pin,
          method: 'GET'
        });
      }
    });
  }

  // Add discovered API endpoints
  if (jsData.api_endpoints && jsData.api_endpoints.length > 0) {
    jsData.api_endpoints.slice(0, 5).forEach((endpoint, index) => {
      const fullUrl = endpoint.startsWith('/') ? baseUrl + endpoint : endpoint;
      if (jsData.scan_guid) {
        const urlWithParams = fullUrl.includes('?') 
          ? `${fullUrl}&scan_guid=${jsData.scan_guid}`
          : `${fullUrl}?scan_guid=${jsData.scan_guid}`;

        analyticsRequests.push({
          url: urlWithParams,
          endpoint_index: index,
          endpoint_type: 'discovered',
          method: 'GET'
        });
      }
    });
  }

  session.scraped_data.api_data.analytics_requests = analyticsRequests;
  session.scraped_data.api_data.total_requests_planned = analyticsRequests.length;

  session.steps.api_calls.status = 'completed';
  session.steps.api_calls.details = {
    competitors_api_success: !!competitorsData,
    competitors_data_type: competitorsData ? typeof competitorsData : 'none',
    analytics_requests_prepared: analyticsRequests.length,
    execution_time_ms: Date.now() - stepStart
  };

  if (session.config.enable_detailed_logging) {
    console.log('‚úÖ API calls completed');
    console.log(`üìä Competitors API: ${competitorsData ? 'Success' : 'Failed'}`);
    console.log(`üîó Analytics requests prepared: ${analyticsRequests.length}`);
    console.log(`‚è±Ô∏è Execution time: ${Date.now() - stepStart}ms`);
  }

  return [{ json: session }];

} catch (error) {
  session.steps.api_calls.status = 'failed';
  session.steps.api_calls.details = {
    error: error.message,
    execution_time_ms: Date.now() - stepStart
  };
  session.errors.push({
    step: 'api_calls',
    error: error.message,
    timestamp: new Date().toISOString()
  });

  console.error('‚ùå API calls failed:', error.message);

  // Continue with partial data
  session.scraped_data.api_data = {
    competitors: null,
    analytics: [],
    status: 'failed',
    error: error.message
  };

  return [{ json: session }];
}

// Comprehensive data assembly and quality assessment
const session = $input.first().json;

const stepStart = Date.now();
session.steps.data_assembly = {
  timestamp: new Date().toISOString(),
  status: 'in_progress'
};

try {
  // Calculate comprehensive metrics
  const metrics = {
    total_execution_time_ms: Date.now() - new Date(session.started_at).getTime(),
    total_elements_found: session.metrics.total_elements_found,
    total_api_calls: session.metrics.total_api_calls || 0,
    data_completeness: {
      scan_information: Object.keys(session.scraped_data.scan_information).length,
      rank_summary: Object.keys(session.scraped_data.rank_summary).length,
      competitors: session.scraped_data.competitors.length,
      sponsored_listings: session.scraped_data.sponsored_listings.length,
      map_data: session.scraped_data.map_data ? session.scraped_data.map_data.length : 0,
      javascript_data: !!session.scraped_data.javascript_data.scan_guid,
      api_data: !!session.scraped_data.api_data
    }
  };

  // Calculate data quality score (0-100)
  let qualityScore = 0;

  // Scan information (25 points max)
  qualityScore += Math.min(25, metrics.data_completeness.scan_information * 5);

  // Competitors data (25 points max)
  qualityScore += Math.min(25, metrics.data_completeness.competitors * 2);

  // JavaScript data (20 points max)
  qualityScore += metrics.data_completeness.javascript_data ? 20 : 0;

  // Rank summary (15 points max)
  qualityScore += Math.min(15, metrics.data_completeness.rank_summary * 3);

  // Map data (10 points max)
  qualityScore += Math.min(10, metrics.data_completeness.map_data * 0.5);

  // API data (5 points max)
  qualityScore += metrics.data_completeness.api_data ? 5 : 0;

  session.metrics.data_quality_score = Math.round(qualityScore);

  // Create final data structure
  const finalData = {
    metadata: {
      session_id: session.session_id,
      scraped_at: new Date().toISOString(),
      source_url: session.config.target_url,
      scraper_version: '1.0.0-n8n-comprehensive',
      method: 'n8n_comprehensive_workflow',
      execution_time_ms: metrics.total_execution_time_ms,
      data_quality_score: session.metrics.data_quality_score,
      success: session.errors.length === 0,
      warnings_count: session.warnings.length,
      errors_count: session.errors.length
    },

    // Main data sections (matching Python scraper output)
    ozet_bilgiler: session.scraped_data.scan_information,
    rank_summary: session.scraped_data.rank_summary,
    rakipler: session.scraped_data.competitors,
    sponsorlu_listeler: session.scraped_data.sponsored_listings,
    detayli_sonuclar: session.scraped_data.detailed_results || [],
    harita_verileri: session.scraped_data.map_data || [],
    javascript_verileri: session.scraped_data.javascript_data,
    api_verileri: session.scraped_data.api_data || {},

    // Quality and diagnostic information
    quality_metrics: {
      completeness_score: session.metrics.data_quality_score,
      elements_found: metrics.data_completeness,
      performance: {
        total_time_ms: metrics.total_execution_time_ms,
        steps_timing: {
          http_request: session.steps.http_request.details?.execution_time_ms || 0,
          html_parsing: session.steps.html_parsing.details?.execution_time_ms || 0,
          js_extraction: session.steps.js_extraction.details?.execution_time_ms || 0,
          api_calls: session.steps.api_calls.details?.execution_time_ms || 0
        }
      }
    },

    // Diagnostic information
    diagnostics: {
      warnings: session.warnings,
      errors: session.errors,
      steps_status: {
        http_request: session.steps.http_request.status,
        html_parsing: session.steps.html_parsing.status,
        js_extraction: session.steps.js_extraction.status,
        api_calls: session.steps.api_calls.status
      },
      configuration: session.config
    }
  };

  session.final_data = finalData;
  session.steps.data_assembly.status = 'completed';
  session.steps.data_assembly.details = {
    final_quality_score: session.metrics.data_quality_score,
    total_data_points: metrics.total_elements_found,
    execution_time_ms: Date.now() - stepStart
  };

  if (session.config.enable_detailed_logging) {
    console.log('‚úÖ Data assembly completed');
    console.log(`üìä Quality score: ${session.metrics.data_quality_score}/100`);
    console.log(`üéØ Data points collected: ${metrics.total_elements_found}`);
    console.log(`‚è±Ô∏è Total execution time: ${metrics.total_execution_time_ms}ms`);
    console.log('üìã Final data structure ready for export');
  }

  return [{ json: session }];

} catch (error) {
  session.steps.data_assembly.status = 'failed';
  session.steps.data_assembly.details = {
    error: error.message,
    execution_time_ms: Date.now() - stepStart
  };
  session.errors.push({
    step: 'data_assembly',
    error: error.message,
    timestamp: new Date().toISOString()
  });

  console.error('‚ùå Data assembly failed:', error.message);
  throw error;
}

