function generateRecommendations(session) {
  const recommendations = [];

  if (session.metrics.data_quality_score < 70) {
    recommendations.push('Data quality is below optimal - consider adjusting CSS selectors or checking site structure');
  }

  if (session.warnings.length > 0) {
    recommendations.push('Multiple warnings detected - review parsing strategies for better data extraction');
  }

  if (session.steps.api_calls.status === 'failed' || session.steps.api_calls.status === 'skipped') {
    recommendations.push('API calls were not successful - verify scan_guid extraction and API endpoints');
  }

  if ((session.scraped_data.competitors || []).length === 0) {
    recommendations.push('No competitors found - check competitors table CSS selectors');
  }

  if (!session.scraped_data.javascript_data.scan_guid) {
    recommendations.push('Scan GUID not found - update JavaScript extraction patterns');
  }

  const totalTime = Date.now() - new Date(session.started_at).getTime();
  if (totalTime > 60000) {
    recommendations.push('Execution time is high - consider optimizing timeout settings and rate limiting');
  }

  if (recommendations.length === 0) {
    recommendations.push('Scraping completed successfully with optimal performance');
  }

  return recommendations;
}

