// js_extractor.py mod√ºl√º fonksiyonalitesi
// JavaScript verilerini √ßƒ±karƒ±r - FIXED VERSION

const htmlContent = $input.first().json.data || $input.first().json.html_content || $input.first().json.body;

// Extract pinz data - Geli≈ütirilmi≈ü regex patterns
function extractPinzData(html) {
  const patterns = [
    /var\s+pinz\s*=\s*(\[[\s\S]*?\]);/i,
    /pinz\s*:\s*(\[[\s\S]*?\])/i,
    /"pinz"\s*:\s*(\[[\s\S]*?\])/i,
    /window\.pinz\s*=\s*(\[[\s\S]*?\]);/i
  ];

  for (const pattern of patterns) {
    const match = html.match(pattern);
    if (match && match[1]) {
      try {
        let jsonStr = match[1]
          .replace(/\\'/g, "'")
          .replace(/\\"/g, '"')
          .replace(/\btrue\b/gi, 'true')
          .replace(/\bfalse\b/gi, 'false')
          .replace(/\bnull\b/gi, 'null')
          .replace(/\bundefined\b/gi, 'null')
          .replace(/,\s*}/g, '}')
          .replace(/,\s*]/g, ']');
        return JSON.parse(jsonStr);
      } catch (e) {
        console.log('Pinz parse error:', e.message);
        continue;
      }
    }
  }
  return [];
}

// Extract scan_guid - Daha robust patterns
function extractScanGuid(html) {
  const patterns = [
    /scan_guid['"]?\s*[:=]\s*['"]([a-f0-9-]{8,})['"]?/i,
    /scan[_-]?guid['"]?\s*[:=]\s*['"]([a-f0-9-]{8,})['"]?/i,
    /"scan_guid"\s*:\s*['"]([a-f0-9-]{8,})['"]?/i,
    /data-scan-guid\s*=\s*['"]([a-f0-9-]{8,})['"]?/i
  ];

  for (const pattern of patterns) {
    const match = html.match(pattern);
    if (match && match[1] && match[1].length > 8) {
      return match[1];
    }
  }
  return '';
}

// Extract place_id - Kesin patterns for Google Place IDs
function extractPlaceId(html) {
  const patterns = [
    /place_id['"]?\s*[:=]\s*['"]([A-Za-z0-9_-]{10,})['"]?/i,
    /"place_id"\s*:\s*['"]([A-Za-z0-9_-]{10,})['"]?/i,
    /data-place-id\s*=\s*['"]([A-Za-z0-9_-]{10,})['"]?/i,
    /(ChIJ[A-Za-z0-9_-]+)/g,
    /google\.maps\.places\.PlaceId\s*=\s*['"]([A-Za-z0-9_-]+)['"]?/i
  ];

  for (const pattern of patterns) {
    const matches = html.match(pattern);
    if (matches) {
      if (pattern.global) {
        // For ChIJ pattern, return first valid match
        for (const match of matches) {
          if (match.startsWith('ChIJ') && match.length > 15) {
            return match;
          }
        }
      } else if (matches[1] && matches[1].length > 10) {
        return matches[1];
      }
    }
  }
  return '';
}

// Validate input data
if (!htmlContent || typeof htmlContent !== 'string') {
  return {
    html_content: '',
    javascript_data: {
      pinz_array: [],
      scan_guid: '',
      place_id: '',
      pinz_count: 0,
      error: 'No valid HTML content found'
    }
  };
}

// Extract all JavaScript data
const pinzData = extractPinzData(htmlContent);
const scanGuid = extractScanGuid(htmlContent);
const placeId = extractPlaceId(htmlContent);

// Additional validation
const isValidGuid = scanGuid.length >= 8 && /^[a-f0-9-]+$/i.test(scanGuid);
const isValidPlaceId = placeId.length >= 10;

return {
  html_content: htmlContent,
  javascript_data: {
    pinz_array: pinzData,
    scan_guid: scanGuid,
    place_id: placeId,
    pinz_count: pinzData.length,
    validation: {
      valid_guid: isValidGuid,
      valid_place_id: isValidPlaceId,
      html_length: htmlContent.length
    }
  }
};

// html_parser.py mod√ºl√º fonksiyonalitesi
// HTML parsing ile √∂zet bilgileri √ßƒ±karƒ±r

const htmlContent = $input.first().json.html_content;
const scanInfo = $input.item(1).json || {};

// Parse scan information
function parseScanInformation(html) {
  const results = {};

  // Extract business name
  const businessNameMatch = html.match(/<h1[^>]*>([^<]+)<\/h1>/i);
  if (businessNameMatch) {
    results['ƒ∞≈ületme Adƒ±'] = businessNameMatch[1].trim();
  }

  // Extract address
  const addressMatch = html.match(/<span[^>]*address[^>]*>([^<]+)<\/span>/i);
  if (addressMatch) {
    results['Adres'] = addressMatch[1].trim();
  }

  // Extract rating
  const ratingMatch = html.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
  if (ratingMatch) {
    results['Puan'] = ratingMatch[1].replace(',', '.');
  }

  // Extract review count
  const reviewMatch = html.match(/\(\s*([0-9]+)\s*(?:Reviews?|Yorum)?\s*\)/i);
  if (reviewMatch) {
    results['Yorum Sayƒ±sƒ±'] = reviewMatch[1];
  }

  return results;
}

// Parse rank summary
function parseRankSummary(html) {
  const results = {};

  // Extract ranked locations
  const rankedMatch = html.match(/Ranked Locations[^>]*>([^<]+)</i);
  if (rankedMatch) {
    results['Ranked Locations'] = rankedMatch[1].trim();
  }

  // Extract average rank
  const avgRankMatch = html.match(/Average rank[^>]*>([^<]+)</i);
  if (avgRankMatch) {
    results['Average rank (Ranked Locations)'] = avgRankMatch[1].trim();
  }

  // Extract best rank
  const bestRankMatch = html.match(/Best rank[^>]*>([^<]+)</i);
  if (bestRankMatch) {
    results['Best rank'] = bestRankMatch[1].trim();
  }

  return results;
}

const scanInformation = parseScanInformation(htmlContent);
const rankSummary = parseRankSummary(htmlContent);

return {
  ozet_bilgiler: {
    ...scanInformation,
    ...rankSummary,
    'Tarih': new Date().toISOString().slice(0, 19).replace('T', ' ')
  }
};

// Competitors parsing (html_parser.py mod√ºl√º)
const competitorsHtml = $input.first().json.data || [];

function parseCompetitors(competitorElements) {
  const competitors = [];

  competitorElements.forEach(element => {
    if (typeof element === 'string' && element.includes('competitor')) {
      const competitor = {};

      // Extract name
      const nameMatch = element.match(/<[^>]*class[^>]*name[^>]*>([^<]+)</i);
      if (nameMatch) competitor['ƒ∞sim'] = nameMatch[1].trim();

      // Extract rating
      const ratingMatch = element.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
      if (ratingMatch) competitor['Puan'] = ratingMatch[1].replace(',', '.');

      // Extract review count
      const reviewMatch = element.match(/\(\s*([0-9]+)\s*(?:Reviews?)?\s*\)/i);
      if (reviewMatch) competitor['Yorum Sayƒ±sƒ±'] = reviewMatch[1];

      // Extract address
      const addressMatch = element.match(/<[^>]*class[^>]*address[^>]*>([^<]+)</i);
      if (addressMatch) competitor['Adres'] = addressMatch[1].trim();

      // Extract website
      const websiteMatch = element.match(/href="([^"]+)"/i);
      if (websiteMatch) competitor['Web Sitesi'] = websiteMatch[1];

      // Set defaults
      competitor['Kategoriler'] = competitor['Kategoriler'] || 'N/A';
      competitor['Fotoƒüraf Sayƒ±sƒ±'] = competitor['Fotoƒüraf Sayƒ±sƒ±'] || '0';
      competitor['Sahiplenme Durumu'] = competitor['Sahiplenme Durumu'] || 'Unknown';
      competitor['Bulunduƒüu Konum Sayƒ±sƒ±'] = competitor['Bulunduƒüu Konum Sayƒ±sƒ±'] || '0';
      competitor['Ortalama Sƒ±ralama'] = competitor['Ortalama Sƒ±ralama'] || '0';

      if (competitor['ƒ∞sim']) {
        competitors.push(competitor);
      }
    }
  });

  return competitors;
}

const competitors = parseCompetitors(competitorsHtml);

return {
  rakipler: competitors
};

// Sponsored listings parsing
const sponsoredHtml = $input.first().json.data || [];

function parseSponsoredListings(sponsoredElements) {
  const sponsored = [];

  sponsoredElements.forEach(element => {
    if (typeof element === 'string') {
      const listing = {};

      // Extract name
      const nameMatch = element.match(/<[^>]*class[^>]*title[^>]*>([^<]+)</i);
      if (nameMatch) listing['ƒ∞sim'] = nameMatch[1].trim();

      // Extract description
      const descMatch = element.match(/<[^>]*class[^>]*description[^>]*>([^<]+)</i);
      if (descMatch) listing['A√ßƒ±klama'] = descMatch[1].trim();

      // Extract link
      const linkMatch = element.match(/href="([^"]+)"/i);
      if (linkMatch) listing['Link'] = linkMatch[1];

      if (listing['ƒ∞sim']) {
        sponsored.push(listing);
      }
    }
  });

  return sponsored;
}

const sponsoredListings = parseSponsoredListings(sponsoredHtml);

return {
  sponsorlu_listeler: sponsoredListings
};

// Detailed results parsing
const detailedHtml = $input.first().json.data || [];

function parseDetailedResults(detailElements) {
  const details = [];

  detailElements.forEach(element => {
    if (typeof element === 'string') {
      const detail = {};

      // Extract ranking position
      const rankMatch = element.match(/rank[^>]*>\s*#?([0-9]+)|position[^>]*>\s*([0-9]+)/i);
      if (rankMatch) detail['Sƒ±ralama'] = rankMatch[1] || rankMatch[2];

      // Extract location name
      const locationMatch = element.match(/location[^>]*>([^<]+)|city[^>]*>([^<]+)/i);
      if (locationMatch) detail['Konum'] = (locationMatch[1] || locationMatch[2]).trim();

      // Extract distance
      const distanceMatch = element.match(/([0-9]+(?:[\.,][0-9]+)?)\s*km/i);
      if (distanceMatch) detail['Mesafe'] = distanceMatch[1] + ' km';

      // Extract search volume
      const volumeMatch = element.match(/volume[^>]*>([^<]+)|searches[^>]*>([^<]+)/i);
      if (volumeMatch) detail['Arama Hacmi'] = (volumeMatch[1] || volumeMatch[2]).trim();

      // Extract business name for this location
      const businessMatch = element.match(/business[^>]*>([^<]+)|name[^>]*>([^<]+)/i);
      if (businessMatch) detail['ƒ∞≈ületme'] = (businessMatch[1] || businessMatch[2]).trim();

      if (detail['Sƒ±ralama'] || detail['Konum'] || detail['ƒ∞≈ületme']) {
        details.push(detail);
      }
    }
  });

  return details;
}

const detailedResults = parseDetailedResults(detailedHtml);

return {
  detayli_sonuclar: detailedResults
};

// Map data parsing
const mapHtml = $input.first().json.data || [];

function parseMapData(mapElements) {
  const mapData = [];

  mapElements.forEach(element => {
    if (typeof element === 'string') {
      const location = {};

      // Extract latitude with simple patterns
      const latMatch = element.match(/data-lat=.([0-9.-]+).|latitude.>([^<]+)/i);
      if (latMatch) location['Enlem'] = latMatch[1] || latMatch[2];

      // Extract longitude with simple patterns
      const lngMatch = element.match(/data-lng=.([0-9.-]+).|longitude.>([^<]+)/i);
      if (lngMatch) location['Boylam'] = lngMatch[1] || lngMatch[2];

      // Extract address
      const addressMatch = element.match(/address[^>]*>([^<]+)/i);
      if (addressMatch) location['Adres'] = addressMatch[1].trim();

      // Extract marker info
      const markerMatch = element.match(/title[^>]*>([^<]+)/i);
      if (markerMatch) location['Marker Bilgisi'] = markerMatch[1];

      if (location['Enlem'] || location['Boylam'] || location['Adres']) {
        mapData.push(location);
      }
    }
  });

  return mapData;
}

const mapResults = parseMapData(mapHtml);

return {
  harita_verileri: mapResults
};

// data_exporter.py mod√ºl√º fonksiyonalitesi - UPDATED VERSION
// T√ºm verileri birle≈ütir ve metadata ekle

const ozet = $input.all().find(item => item.json.ozet_bilgiler)?.json.ozet_bilgiler || {};
const rakipler = $input.all().find(item => item.json.rakipler)?.json.rakipler || [];
const sponsorlu = $input.all().find(item => item.json.sponsorlu_listeler)?.json.sponsorlu_listeler || [];
const detayliSonuclar = $input.all().find(item => item.json.detayli_sonuclar)?.json.detayli_sonuclar || [];
const haritaVerileri = $input.all().find(item => item.json.harita_verileri)?.json.harita_verileri || [];
const jsData = $input.all().find(item => item.json.javascript_data)?.json.javascript_data || {};
const apiData = $input.all().find(item => item.json.api_data)?.json.api_data || {};

// Enhanced map data from JavaScript pinz array
const enhancedMapData = [...haritaVerileri];
if (jsData.pinz_array && jsData.pinz_array.length > 0) {
  jsData.pinz_array.forEach((pin, index) => {
    enhancedMapData.push({
      'Index': index + 1,
      'Source': 'JavaScript',
      'Latitude': pin.lat || 'N/A',
      'Longitude': pin.lng || 'N/A',
      'Title': pin.title || 'N/A',
      'URL': pin.url || 'N/A',
      'Rank': pin.rank || 'N/A',
      'Distance': pin.distance || 'N/A'
    });
  });
}

// Enhanced JavaScript data with validation
const enhancedJsData = {
  pinz: jsData.pinz_array || [],
  scan_guid: jsData.scan_guid || '',
  place_id: jsData.place_id || '',
  validation: jsData.validation || {},
  pinz_count: (jsData.pinz_array || []).length
};

// Enhanced API data
const enhancedApiData = {
  analytics_data: apiData.analytics_data || [],
  competitors_data: apiData.competitors_data || [],
  ranking_data: apiData.ranking_data || []
};

// Final data structure - COMPLETE
const finalData = {
  ozet_bilgiler: ozet,
  rakipler: rakipler,
  sponsorlu_listeler: sponsorlu,
  detayli_sonuclar: detayliSonuclar,
  harita_verileri: enhancedMapData,
  api_verileri: enhancedApiData,
  javascript_verileri: enhancedJsData,
  metadata: {
    scraped_at: new Date().toISOString(),
    url: 'https://www.local-rank.report/scan/97919fde-e478-4081-983f-7e0065b6b5bb',
    scraper_version: '4.1',
    method: 'modular_hybrid_enhanced',
    selenium_used: false,
    total_competitors: rakipler.length,
    total_sponsored: sponsorlu.length,
    total_detailed_results: detayliSonuclar.length,
    total_map_points: enhancedMapData.length,
    parsing_success: {
      js_data: Object.keys(jsData).length > 0,
      competitors: rakipler.length > 0,
      sponsored: sponsorlu.length > 0,
      summary: Object.keys(ozet).length > 0
    }
  }
};

return {
  data: JSON.stringify(finalData, null, 2)
};

