// js_extractor.py modülü fonksiyonalitesi
// JavaScript verilerini çıkarır

const htmlContent = items[0].json.data;

// Extract pinz data
function extractPinzData(html) {
  const pinzRegex = /var\s+pinz\s*=\s*(\[.*?\]);/s;
  const match = html.match(pinzRegex);
  if (match) {
    try {
      // JavaScript boolean/null values'ı Python eşdeğerleriyle değiştir
      let jsonStr = match[1]
        .replace(/\btrue\b/gi, 'true')
        .replace(/\bfalse\b/gi, 'false')
        .replace(/\bnull\b/gi, 'null');
      return JSON.parse(jsonStr);
    } catch (e) {
      return [];
    }
  }
  return [];
}

// Extract scan_guid
function extractScanGuid(html) {
  const scanMatch = html.match(/scan_guid['"]?\s*[:=]\s*['"]([^'"]+)['"]/i);
  return scanMatch ? scanMatch[1] : '';
}

// Extract place_id
function extractPlaceId(html) {
  const placeMatch = html.match(/place_id['"]?\s*[:=]\s*['"]([^'"]+)['"]/i);
  return placeMatch ? placeMatch[1] : '';
}

// Extract all JavaScript data
const pinzData = extractPinzData(htmlContent);
const scanGuid = extractScanGuid(htmlContent);
const placeId = extractPlaceId(htmlContent);

return {
  json: {
    html_content: htmlContent,
    javascript_data: {
      pinz_array: pinzData,
      scan_guid: scanGuid,
      place_id: placeId,
      pinz_count: pinzData.length
    }
  }
};

// html_parser.py modülü fonksiyonalitesi
// HTML parsing ile özet bilgileri çıkarır

const htmlContent = items[0].json.html_content;
const scanInfo = items[1].json || {};

// Parse scan information
function parseScanInformation(html) {
  const results = {};

  // Extract business name
  const businessNameMatch = html.match(/<h1[^>]*>([^<]+)<\/h1>/i);
  if (businessNameMatch) {
    results['İşletme Adı'] = businessNameMatch[1].trim();
  }

  // Extract address
  const addressMatch = html.match(/<span[^>]*address[^>]*>([^<]+)<\/span>/i);
  if (addressMatch) {
    results['Adres'] = addressMatch[1].trim();
  }

  // Extract rating
  const ratingMatch = html.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
  if (ratingMatch) {
    results['Puan'] = ratingMatch[1].replace(',', '.');
  }

  // Extract review count
  const reviewMatch = html.match(/\(\s*([0-9]+)\s*(?:Reviews?|Yorum)?\s*\)/i);
  if (reviewMatch) {
    results['Yorum Sayısı'] = reviewMatch[1];
  }

  return results;
}

// Parse rank summary
function parseRankSummary(html) {
  const results = {};

  // Extract ranked locations
  const rankedMatch = html.match(/Ranked Locations[^>]*>([^<]+)</i);
  if (rankedMatch) {
    results['Ranked Locations'] = rankedMatch[1].trim();
  }

  // Extract average rank
  const avgRankMatch = html.match(/Average rank[^>]*>([^<]+)</i);
  if (avgRankMatch) {
    results['Average rank (Ranked Locations)'] = avgRankMatch[1].trim();
  }

  // Extract best rank
  const bestRankMatch = html.match(/Best rank[^>]*>([^<]+)</i);
  if (bestRankMatch) {
    results['Best rank'] = bestRankMatch[1].trim();
  }

  return results;
}

const scanInformation = parseScanInformation(htmlContent);
const rankSummary = parseRankSummary(htmlContent);

return {
  json: {
    ozet_bilgiler: {
      ...scanInformation,
      ...rankSummary,
      'Tarih': new Date().toISOString().slice(0, 19).replace('T', ' ')
    }
  }
};

// Competitors parsing (html_parser.py modülü)
const competitorsHtml = items[0].json.data || [];

function parseCompetitors(competitorElements) {
  const competitors = [];

  competitorElements.forEach(element => {
    if (typeof element === 'string' && element.includes('competitor')) {
      const competitor = {};

      // Extract name
      const nameMatch = element.match(/<[^>]*class[^>]*name[^>]*>([^<]+)</i);
      if (nameMatch) competitor['İsim'] = nameMatch[1].trim();

      // Extract rating
      const ratingMatch = element.match(/title="([0-9]+(?:[\.,][0-9]+)?)\s*out\s*of\s*5"/i);
      if (ratingMatch) competitor['Puan'] = ratingMatch[1].replace(',', '.');

      // Extract review count
      const reviewMatch = element.match(/\(\s*([0-9]+)\s*(?:Reviews?)?\s*\)/i);
      if (reviewMatch) competitor['Yorum Sayısı'] = reviewMatch[1];

      // Extract address
      const addressMatch = element.match(/<[^>]*class[^>]*address[^>]*>([^<]+)</i);
      if (addressMatch) competitor['Adres'] = addressMatch[1].trim();

      // Extract website
      const websiteMatch = element.match(/href="([^"]+)"/i);
      if (websiteMatch) competitor['Web Sitesi'] = websiteMatch[1];

      // Set defaults
      competitor['Kategoriler'] = competitor['Kategoriler'] || 'N/A';
      competitor['Fotoğraf Sayısı'] = competitor['Fotoğraf Sayısı'] || '0';
      competitor['Sahiplenme Durumu'] = competitor['Sahiplenme Durumu'] || 'Unknown';
      competitor['Bulunduğu Konum Sayısı'] = competitor['Bulunduğu Konum Sayısı'] || '0';
      competitor['Ortalama Sıralama'] = competitor['Ortalama Sıralama'] || '0';

      if (competitor['İsim']) {
        competitors.push(competitor);
      }
    }
  });

  return competitors;
}

const competitors = parseCompetitors(competitorsHtml);

return {
  json: {
    rakipler: competitors
  }
};

// Sponsored listings parsing
const sponsoredHtml = items[0].json.data || [];

function parseSponsoredListings(sponsoredElements) {
  const sponsored = [];

  sponsoredElements.forEach(element => {
    if (typeof element === 'string') {
      const listing = {};

      // Extract name
      const nameMatch = element.match(/<[^>]*class[^>]*title[^>]*>([^<]+)</i);
      if (nameMatch) listing['İsim'] = nameMatch[1].trim();

      // Extract description
      const descMatch = element.match(/<[^>]*class[^>]*description[^>]*>([^<]+)</i);
      if (descMatch) listing['Açıklama'] = descMatch[1].trim();

      // Extract link
      const linkMatch = element.match(/href="([^"]+)"/i);
      if (linkMatch) listing['Link'] = linkMatch[1];

      if (listing['İsim']) {
        sponsored.push(listing);
      }
    }
  });

  return sponsored;
}

const sponsoredListings = parseSponsoredListings(sponsoredHtml);

return {
  json: {
    sponsorlu_listeler: sponsoredListings
  }
};

// API Analytics calls for each pinz location
const jsData = items[0].json.javascript_data;
const baseUrl = 'https://www.local-rank.report';

const apiRequests = [];

if (jsData.pinz_array && jsData.pinz_array.length > 0) {
  jsData.pinz_array.forEach((pin, index) => {
    if (pin.url) {
      apiRequests.push({
        url: baseUrl + pin.url,
        method: 'GET',
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
          'Accept': 'application/json,text/html'
        },
        location_index: index,
        location_data: pin
      });
    }
  });
}

return apiRequests.map(req => ({ json: req }));

// data_exporter.py modülü fonksiyonalitesi
// Tüm verileri birleştir ve metadata ekle

const ozet = items.find(item => item.json.ozet_bilgiler)?.json.ozet_bilgiler || {};
const rakipler = items.find(item => item.json.rakipler)?.json.rakipler || [];
const sponsorlu = items.find(item => item.json.sponsorlu_listeler)?.json.sponsorlu_listeler || [];
const jsData = items.find(item => item.json.javascript_data)?.json.javascript_data || {};

// Map verileri çıkar
const haritaVerileri = [];
if (jsData.pinz_array) {
  jsData.pinz_array.forEach((pin, index) => {
    haritaVerileri.push({
      'Index': index + 1,
      'Latitude': pin.lat || 'N/A',
      'Longitude': pin.lng || 'N/A',
      'Title': pin.title || 'N/A',
      'URL': pin.url || 'N/A'
    });
  });
}

// Final data structure
const finalData = {
  ozet_bilgiler: ozet,
  rakipler: rakipler,
  sponsorlu_listeler: sponsorlu,
  detayli_sonuclar: [], // API'den gelecek
  harita_verileri: haritaVerileri,
  javascript_verileri: {
    scan_guid: jsData.scan_guid || '',
    place_id: jsData.place_id || '',
    pinz_count: jsData.pinz_count || 0
  },
  api_verileri: [], // API'den gelecek
  metadata: {
    scraped_at: new Date().toISOString(),
    url: 'https://www.local-rank.report/scan/97919fde-e478-4081-983f-7e0065b6b5bb',
    scraper_version: '4.0',
    method: 'n8n_modular_hybrid',
    total_competitors: rakipler.length,
    total_locations: haritaVerileri.length
  }
};

return { json: finalData };

// Rate limiting function
const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

const rateLimit = 1000; // 1 saniye bekleme
const randomDelay = Math.random() * 500; // 0-500ms rastgele ekleme

await delay(rateLimit + randomDelay);

return items;

// Rakipler verisini dizi formatına çevir
const data = items[0].json;

if (data.rakipler && Array.isArray(data.rakipler) && data.rakipler.length > 0) {
  return data.rakipler.map(rakip => ({ json: rakip }));
}

return [];

// Sponsorlu listeler verisini dizi formatına çevir
const data = items[0].json;

if (data.sponsorlu_listeler && Array.isArray(data.sponsorlu_listeler) && data.sponsorlu_listeler.length > 0) {
  return data.sponsorlu_listeler.map(sponsor => ({ json: sponsor }));
}

return [];

// Harita verileri için dizi formatına çevir
const data = items[0].json;

if (data.harita_verileri && Array.isArray(data.harita_verileri) && data.harita_verileri.length > 0) {
  return data.harita_verileri.map(harita => ({ json: harita }));
}

return [];
