// Initialize test suite
const config = $input.first().json;

const testSuite = {
  test_id: `test_${Date.now()}`,
  started_at: new Date().toISOString(),
  test_url: config.test_url,
  run_full_test: config.run_full_test,
  validate_data_quality: config.validate_data_quality,
  tests: {
    connectivity: { status: 'pending', details: null },
    html_structure: { status: 'pending', details: null },
    javascript_data: { status: 'pending', details: null },
    api_endpoints: { status: 'pending', details: null },
    data_quality: { status: 'pending', details: null }
  },
  overall_status: 'running'
};

console.log('ðŸ§ª Starting validation test suite:', testSuite.test_id);

return [{ json: testSuite }];

// Validate connectivity test
const response = $input.first().json;
const testSuite = $input.last().json;

try {
  // Check response
  if (!response || typeof response !== 'string') {
    throw new Error('Invalid response format');
  }

  if (response.length < 1000) {
    throw new Error('Response too short - possible error page');
  }

  if (response.includes('404') || response.includes('Not Found')) {
    throw new Error('404 error detected');
  }

  if (response.includes('captcha') || response.includes('blocked')) {
    throw new Error('Bot detection triggered');
  }

  // Success
  testSuite.tests.connectivity = {
    status: 'passed',
    details: {
      response_size: response.length,
      response_type: typeof response,
      contains_html: response.includes('<html>'),
      contains_scripts: response.includes('<script>')
    }
  };

  console.log('âœ… Connectivity test passed');

} catch (error) {
  testSuite.tests.connectivity = {
    status: 'failed',
    details: {
      error: error.message,
      response_received: !!response,
      response_size: response ? response.length : 0
    }
  };

  console.error('âŒ Connectivity test failed:', error.message);
}

return [{ json: { ...testSuite, html_content: response } }];

// Validate HTML structure test
const extractedData = $input.first().json;
const testSuite = $input.last().json;

try {
  const requiredElements = {
    page_title: 'Page title',
    scan_info_table: 'Scan information table',
    rank_summary_table: 'Rank summary table',
    competitors_table: 'Competitors table',
    all_scripts: 'Script tags'
  };

  const results = {};
  const missing = [];

  for (const [key, description] of Object.entries(requiredElements)) {
    const found = extractedData[key] && extractedData[key].length > 0;
    results[key] = {
      found: found,
      size: found ? extractedData[key].length : 0,
      description: description
    };

    if (!found) {
      missing.push(description);
    }
  }

  // Check if critical elements are present
  const criticalMissing = missing.filter(item => 
    item.includes('table') || item.includes('Script')
  );

  if (criticalMissing.length > 0) {
    throw new Error(`Critical elements missing: ${criticalMissing.join(', ')}`);
  }

  // Success
  testSuite.tests.html_structure = {
    status: 'passed',
    details: {
      elements_found: Object.keys(results).length,
      missing_elements: missing,
      element_details: results
    }
  };

  console.log('âœ… HTML structure test passed');

} catch (error) {
  testSuite.tests.html_structure = {
    status: 'failed',
    details: {
      error: error.message,
      extracted_keys: Object.keys(extractedData)
    }
  };

  console.error('âŒ HTML structure test failed:', error.message);
}

return [{ json: { ...testSuite, extracted_html: extractedData } }];

// Test JavaScript data extraction
const testData = $input.first().json;
const scriptsHtml = testData.extracted_html?.all_scripts;

try {
  if (!scriptsHtml) {
    throw new Error('No script tags found for testing');
  }

  const cheerio = require('cheerio');
  const $ = cheerio.load(scriptsHtml);

  let jsData = {
    pinz: [],
    scan_guid: null,
    place_id: null
  };

  let scriptsProcessed = 0;
  let dataFound = false;

  $('script').each((i, script) => {
    scriptsProcessed++;
    const scriptText = $(script).html() || '';

    // Test pinz array extraction
    const pinzMatch = scriptText.match(/var\s+pinz\s*=\s*(\[.*?\]);/s);
    if (pinzMatch) {
      try {
        jsData.pinz = JSON.parse(pinzMatch[1]);
        dataFound = true;
      } catch (e) {
        console.log('Pinz parsing error:', e.message);
      }
    }

    // Test scan_guid extraction
    const scanGuidMatch = scriptText.match(/scan_guid['"]?\s*[:=]\s*['"]([^'"]+)['"]/i);
    if (scanGuidMatch) {
      jsData.scan_guid = scanGuidMatch[1];
      dataFound = true;
    }

    // Test place_id extraction
    const placeIdMatch = scriptText.match(/place_id['"]?\s*[:=]\s*['"]([^'"]+)['"]/i);
    if (placeIdMatch) {
      jsData.place_id = placeIdMatch[1];
    }
  });

  if (!dataFound) {
    throw new Error('No JavaScript data found in any script tag');
  }

  // Success
  testData.tests.javascript_data = {
    status: 'passed',
    details: {
      scripts_processed: scriptsProcessed,
      pinz_count: jsData.pinz.length,
      scan_guid_found: !!jsData.scan_guid,
      place_id_found: !!jsData.place_id,
      extracted_data: jsData
    }
  };

  console.log('âœ… JavaScript data test passed');

} catch (error) {
  testData.tests.javascript_data = {
    status: 'failed',
    details: {
      error: error.message,
      scripts_available: !!scriptsHtml
    }
  };

  console.error('âŒ JavaScript data test failed:', error.message);
}

return [{ json: testData }];

// Test API endpoints (if JavaScript data is available)
const testData = $input.first().json;
const jsTestResult = testData.tests.javascript_data;

if (jsTestResult.status !== 'passed' || !jsTestResult.details.scan_guid_found) {
  testData.tests.api_endpoints = {
    status: 'skipped',
    details: {
      reason: 'JavaScript data test failed or scan_guid not found'
    }
  };

  console.log('â­ï¸ API endpoints test skipped');
  return [{ json: testData }];
}

try {
  const jsData = jsTestResult.details.extracted_data;
  const baseUrl = testData.test_url.match(/https?:\/\/[^/]+/)[0];

  // Prepare API test data
  const apiTests = {
    competitors_endpoint: {
      url: `${baseUrl}/scans/get-competitors-list?scan_guid=${jsData.scan_guid}`,
      expected_response: 'array or object with data property'
    }
  };

  testData.tests.api_endpoints = {
    status: 'ready_for_testing',
    details: {
      base_url: baseUrl,
      scan_guid: jsData.scan_guid,
      endpoints_to_test: Object.keys(apiTests),
      test_data: apiTests
    }
  };

  console.log('ðŸ”„ API endpoints ready for testing');

} catch (error) {
  testData.tests.api_endpoints = {
    status: 'failed',
    details: {
      error: error.message
    }
  };

  console.error('âŒ API endpoints test preparation failed:', error.message);
}

return [{ json: testData }];

// Perform comprehensive data quality validation
const testData = $input.first().json;

try {
  const quality = {
    connectivity: testData.tests.connectivity.status === 'passed',
    html_structure: testData.tests.html_structure.status === 'passed',
    javascript_data: testData.tests.javascript_data.status === 'passed',
    critical_data_available: false,
    overall_score: 0
  };

  // Check critical data availability
  if (quality.html_structure && quality.javascript_data) {
    const htmlDetails = testData.tests.html_structure.details;
    const jsDetails = testData.tests.javascript_data.details;

    quality.critical_data_available = 
      htmlDetails.element_details.scan_info_table?.found &&
      htmlDetails.element_details.competitors_table?.found &&
      jsDetails.scan_guid_found &&
      jsDetails.pinz_count > 0;
  }

  // Calculate overall score
  const scores = {
    connectivity: quality.connectivity ? 25 : 0,
    html_structure: quality.html_structure ? 25 : 0,
    javascript_data: quality.javascript_data ? 25 : 0,
    critical_data: quality.critical_data_available ? 25 : 0
  };

  quality.overall_score = Object.values(scores).reduce((a, b) => a + b, 0);

  // Determine overall status
  let overallStatus = 'failed';
  if (quality.overall_score >= 90) {
    overallStatus = 'excellent';
  } else if (quality.overall_score >= 75) {
    overallStatus = 'good';
  } else if (quality.overall_score >= 50) {
    overallStatus = 'acceptable';
  }

  testData.tests.data_quality = {
    status: 'completed',
    details: {
      quality_checks: quality,
      score_breakdown: scores,
      overall_score: quality.overall_score,
      overall_status: overallStatus,
      recommendations: getRecommendations(quality, testData.tests)
    }
  };

  testData.overall_status = overallStatus;
  testData.completed_at = new Date().toISOString();

  console.log(`ðŸŽ¯ Data quality validation completed: ${overallStatus} (${quality.overall_score}%)`);

} catch (error) {
  testData.tests.data_quality = {
    status: 'failed',
    details: {
      error: error.message
    }
  };

  testData.overall_status = 'failed';
  console.error('âŒ Data quality validation failed:', error.message);
}

function getRecommendations(quality, tests) {
  const recommendations = [];

  if (!quality.connectivity) {
    recommendations.push('Check network connectivity and URL accessibility');
  }

  if (!quality.html_structure) {
    recommendations.push('Update CSS selectors - page structure may have changed');
  }

  if (!quality.javascript_data) {
    recommendations.push('Update JavaScript regex patterns - script structure may have changed');
  }

  if (!quality.critical_data_available) {
    recommendations.push('Critical data missing - verify site functionality manually');
  }

  if (tests.api_endpoints?.status === 'failed') {
    recommendations.push('Check API endpoints and authentication requirements');
  }

  if (recommendations.length === 0) {
    recommendations.push('All tests passed - scraper is ready for production use');
  }

  return recommendations;
}

return [{ json: testData }];

// Generate comprehensive test report
const testData = $input.first().json;

const report = {
  test_summary: {
    test_id: testData.test_id,
    started_at: testData.started_at,
    completed_at: testData.completed_at,
    duration_ms: new Date(testData.completed_at) - new Date(testData.started_at),
    overall_status: testData.overall_status,
    overall_score: testData.tests.data_quality.details?.overall_score || 0
  },

  test_results: {
    connectivity: {
      status: testData.tests.connectivity.status,
      details: testData.tests.connectivity.details
    },
    html_structure: {
      status: testData.tests.html_structure.status,
      details: testData.tests.html_structure.details
    },
    javascript_data: {
      status: testData.tests.javascript_data.status,
      details: testData.tests.javascript_data.details
    },
    api_endpoints: {
      status: testData.tests.api_endpoints.status,
      details: testData.tests.api_endpoints.details
    },
    data_quality: {
      status: testData.tests.data_quality.status,
      details: testData.tests.data_quality.details
    }
  },

  recommendations: testData.tests.data_quality.details?.recommendations || [],

  production_readiness: {
    ready: testData.overall_status === 'excellent' || testData.overall_status === 'good',
    confidence_level: testData.overall_status,
    required_actions: testData.tests.data_quality.details?.recommendations?.filter(r => 
      r.includes('Check') || r.includes('Update')
    ) || []
  }
};

// Log results
console.log('\nðŸ§ª TEST REPORT SUMMARY');
console.log('========================');
console.log(`Overall Status: ${report.test_summary.overall_status.toUpperCase()}`);
console.log(`Score: ${report.test_summary.overall_score}%`);
console.log(`Duration: ${report.test_summary.duration_ms}ms`);
console.log(`Production Ready: ${report.production_readiness.ready ? 'YES' : 'NO'}`);

if (report.recommendations.length > 0) {
  console.log('\nRecommendations:');
  report.recommendations.forEach((rec, i) => {
    console.log(`${i + 1}. ${rec}`);
  });
}

return [{ json: report }];

// Create human-readable summary
const report = $input.first().json;

const summary = `
# ðŸ§ª Local Rank Report Scraper - Validation Report

## ðŸ“Š Test Summary
- **Test ID:** ${report.test_summary.test_id}
- **Overall Status:** ${report.test_summary.overall_status.toUpperCase()}
- **Score:** ${report.test_summary.overall_score}%
- **Duration:** ${report.test_summary.duration_ms}ms
- **Production Ready:** ${report.production_readiness.ready ? 'âœ… YES' : 'âŒ NO'}

## ðŸ” Test Results

### Connectivity Test
- **Status:** ${report.test_results.connectivity.status === 'passed' ? 'âœ… PASSED' : 'âŒ FAILED'}
- **Response Size:** ${report.test_results.connectivity.details?.response_size || 'N/A'} bytes

### HTML Structure Test
- **Status:** ${report.test_results.html_structure.status === 'passed' ? 'âœ… PASSED' : 'âŒ FAILED'}
- **Elements Found:** ${report.test_results.html_structure.details?.elements_found || 0}

### JavaScript Data Test
- **Status:** ${report.test_results.javascript_data.status === 'passed' ? 'âœ… PASSED' : 'âŒ FAILED'}
- **Pinz Count:** ${report.test_results.javascript_data.details?.pinz_count || 0}
- **Scan GUID Found:** ${report.test_results.javascript_data.details?.scan_guid_found ? 'Yes' : 'No'}

### API Endpoints Test
- **Status:** ${report.test_results.api_endpoints.status || 'Not tested'}

## ðŸ’¡ Recommendations

${report.recommendations.map((rec, i) => `${i + 1}. ${rec}`).join('\n')}

## ðŸŽ¯ Next Steps

${report.production_readiness.ready ? 
  'The scraper is ready for production use. You can proceed with confidence.' : 
  'Please address the recommendations above before using in production.'}

---
*Generated on ${new Date().toISOString()}*
`;

return [{ json: { summary: summary } }];
